			from	w_pls_localizar_sca
			where	nr_seq_sca	= nr_seq_sca_p
			and	nr_seq_simulacao		= nr_seq_vinculo_p;

			if (qt_registro_w = 0) then
				insert into w_pls_localizar_sca(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
						nr_seq_sca,nr_seq_simulacao,cd_estabelecimento,nr_seq_tabela)
				values (	nextval('w_pls_localizar_sca_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
						nr_seq_sca_p,nr_seq_vinculo_p,cd_estabelecimento_p,nr_seq_tabela_w);
			elsif (qt_registro_w > 0) then
				update	w_pls_localizar_sca
				set	nr_seq_tabela = nr_seq_tabela_w
				where	nr_seq_sca	= nr_seq_sca_p
				and	nr_seq_simulacao		= nr_seq_vinculo_p;
			end if;
		end if;
	elsif (ie_opcao_p	= 'CO') then
		if (nr_seq_sca_p IS NOT NULL AND nr_seq_sca_p::text <> '') then
			select	count(*)
			into STRICT	qt_registro_w
			from	w_pls_localizar_sca
			where	nr_seq_sca	= nr_seq_sca_p
			and	nr_seq_contrato		= nr_seq_vinculo_p;

			if (qt_registro_w = 0) then
				insert into w_pls_localizar_sca(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
						nr_seq_sca,nr_seq_contrato,cd_estabelecimento,nr_seq_tabela)
				values (	nextval('w_pls_localizar_sca_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
						nr_seq_sca_p,nr_seq_vinculo_p,cd_estabelecimento_p,nr_seq_tabela_w);
			elsif (qt_registro_w > 0) then
				update	w_pls_localizar_sca
				set	nr_seq_tabela = nr_seq_tabela_w
				where	nr_seq_sca	= nr_seq_sca_p
				and	nr_seq_contrato		= nr_seq_vinculo_p;
			end if;
		end if;
	elsif (ie_opcao_p	= 'PL') then
		if (nr_seq_sca_p IS NOT NULL AND nr_seq_sca_p::text <> '') then
			select	count(*)
			into STRICT	qt_registro_w
			from	w_pls_localizar_sca
			where	nr_seq_sca	= nr_seq_sca_p
			and	nr_seq_plano		= nr_seq_vinculo_p;

			if (qt_registro_w = 0) then
				insert into w_pls_localizar_sca(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
						nr_seq_sca,nr_seq_plano,cd_estabelecimento,nr_seq_tabela)
				values (	nextval('w_pls_localizar_sca_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
						nr_seq_sca_p,nr_seq_vinculo_p,cd_estabelecimento_p,nr_seq_tabela_w);
			elsif (qt_registro_w > 0) then
				update	w_pls_localizar_sca
				set	nr_seq_tabela = nr_seq_tabela_w
				where	nr_seq_sca	= nr_seq_sca_p
				and	nr_seq_plano		= nr_seq_vinculo_p;
			end if;
		end if;
	end if;
elsif (ie_tipo_acao_p	= 'D') then
	if (ie_opcao_p	= 'BE') then
		if (nr_seq_sca_p IS NOT NULL AND nr_seq_sca_p::text <> '') then
			select	count(*)
			into STRICT	qt_registro_w
			from	w_pls_localizar_sca
			where	nr_seq_sca	= nr_seq_sca_p
			and	nr_seq_segurado		= nr_seq_vinculo_p;

			if (qt_registro_w > 0) then
				delete	FROM w_pls_localizar_sca
				where	nr_seq_sca	= nr_seq_sca_p
				and	nr_seq_segurado		= nr_seq_vinculo_p;
			end if;
		end if;
	elsif (ie_opcao_p	= 'BP') then
		if (nr_seq_sca_p IS NOT NULL AND nr_seq_sca_p::text <> '') then
			select	count(*)
			into STRICT	qt_registro_w
			from	w_pls_localizar_sca
			where	nr_seq_sca		= nr_seq_sca_p
			and	nr_seq_segurado_prop	= nr_seq_vinculo_p;

			if (qt_registro_w > 0) then
				delete	FROM w_pls_localizar_sca
				where	nr_seq_sca	= nr_seq_sca_p
				and	nr_seq_segurado_prop	= nr_seq_vinculo_p;
			end if;
		end if;
	elsif (ie_opcao_p	= 'SP') then
		if (nr_seq_sca_p IS NOT NULL AND nr_seq_sca_p::text <> '') then
			select	count(*)
