	elsif (ie_opcao_p	= 'CO') then
		if (nr_seq_sca_p IS NOT NULL AND nr_seq_sca_p::text <> '') then
			select	count(*)
			into STRICT	qt_registro_w
			from	w_pls_localizar_sca
			where	nr_seq_sca	= nr_seq_sca_p
			and	nr_seq_contrato	= nr_seq_vinculo_p;

			if (qt_registro_w > 0) then
