SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_cat_obter_dados_empregador ( nr_seq_estipulante_p bigint, nr_seq_contrato_p bigint, ie_tipo_documento_p INOUT bigint, nm_empregador_p INOUT text, cd_documento_p INOUT text, ds_endereco_p INOUT text, ds_bairro_p INOUT text, cd_cep_p INOUT text, nm_municipio_p INOUT text, sg_estado_p INOUT text, nr_telefone_p INOUT text, ds_complemento_p INOUT text) AS $body$
DECLARE


nr_seq_contrato_w			bigint;
nr_seq_subestipulante_w		bigint;
cd_cgc_w			varchar(14);
cd_pessoa_fisica_w		varchar(10);
ie_tipo_documento_w		smallint	:= 0;
cd_documento_w			varchar(14)	:= '';
nm_empregador_w			pessoa_juridica.ds_razao_social%type;
ds_endereco_w			varchar(100);
ds_bairro_w			varchar(40);
cd_cep_w			varchar(15);
nm_municipio_w			varchar(100);
sg_estado_w			pessoa_juridica.sg_estado%type;
nr_telefone_w			varchar(15);
ds_complemento_w			varchar(40);


BEGIN

/* Obter o estipulante */

if (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then
	nr_seq_contrato_w := nr_seq_contrato_p;
	nr_seq_subestipulante_w := 0;
elsif (nr_seq_estipulante_p IS NOT NULL AND nr_seq_estipulante_p::text <> '') then
	select	nr_seq_contrato,
		coalesce(nr_seq_subestipulante,0)
	into STRICT	nr_seq_contrato_w,
		nr_seq_subestipulante_w
	from	pls_estipulante_web
	where	nr_sequencia	= nr_seq_estipulante_p;
end if;

if (nr_seq_subestipulante_w > 0) then
	begin
	select	coalesce(cd_cgc,''),
		coalesce(cd_pessoa_fisica, '')
	into STRICT	cd_cgc_w,
		cd_pessoa_fisica_w
	from	pls_sub_estipulante
	where	nr_sequencia	= nr_seq_subestipulante_w;
	exception
		when others then
		cd_cgc_w		:= '';
		cd_pessoa_fisica_w	:= '';
	end;
else
	begin
	select	coalesce(cd_cgc_estipulante,''),
		coalesce(cd_pf_estipulante, '')
	into STRICT	cd_cgc_w,
		cd_pessoa_fisica_w
	from	pls_contrato
	where	nr_sequencia	= nr_seq_contrato_w;
	exception
		when others then
		cd_cgc_w		:= '';
		cd_pessoa_fisica_w	:= '';
	end;
end if;

if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
	ie_tipo_documento_w	:= 1;
	cd_documento_w		:= cd_cgc_w;

	select	ds_razao_social,
		ds_endereco,
		ds_bairro,
		cd_cep,
		ds_municipio,
		sg_estado,
		nr_telefone,
		ds_complemento
	into STRICT	nm_empregador_w,
		ds_endereco_w,
		ds_bairro_w,
		cd_cep_w,
		nm_municipio_w,
		sg_estado_w,
		nr_telefone_w,
		ds_complemento_w
	from	pessoa_juridica
	where	cd_cgc	= cd_cgc_w;
elsif (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	ie_tipo_documento_w	:= 3;
	
	select	a.nm_pessoa_fisica,
		a.nr_cpf,
		b.ds_endereco,
		substr(b.ds_bairro,1,40),
		b.cd_cep,
		b.ds_municipio,
		b.sg_estado,
		b.nr_telefone,
		b.ds_complemento
	into STRICT	nm_empregador_w,
		cd_documento_w,
		ds_endereco_w,
		ds_bairro_w,
		cd_cep_w,
		nm_municipio_w,
		sg_estado_w,
		nr_telefone_w,
		ds_complemento_w
	from	compl_pessoa_fisica	b,
		pessoa_fisica		a
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
	and	b.ie_tipo_complemento	= 1
	and	a.cd_pessoa_fisica	= cd_pessoa_fisica_w;
end if;

ie_tipo_documento_p	:= ie_tipo_documento_w;
nm_empregador_p		:= nm_empregador_w;
cd_documento_p		:= cd_documento_w;
ds_endereco_p		:= ds_endereco_w;
ds_complemento_p	:= ds_complemento_w;
ds_bairro_p		:= ds_bairro_w;
cd_cep_p		:= cd_cep_w;
nm_municipio_p		:= nm_municipio_w;
sg_estado_p		:= sg_estado_w;
nr_telefone_p		:= nr_telefone_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_cat_obter_dados_empregador ( nr_seq_estipulante_p bigint, nr_seq_contrato_p bigint, ie_tipo_documento_p INOUT bigint, nm_empregador_p INOUT text, cd_documento_p INOUT text, ds_endereco_p INOUT text, ds_bairro_p INOUT text, cd_cep_p INOUT text, nm_municipio_p INOUT text, sg_estado_p INOUT text, nr_telefone_p INOUT text, ds_complemento_p INOUT text) FROM PUBLIC;

