CREATE OR REPLACE PROCEDURE pls_chamado_etapa_acao ( nr_seq_chamado_etapa_p pls_chamado_etapa.nr_sequencia%type, ie_acao_p text, nm_usuario_p usuario.nm_usuario%type, nr_seq_motivo_encerramento_p pls_motivo_final_chamado.nr_sequencia%type, nr_seq_historico_p pls_chamado_etapa_hist.nr_sequencia%type) AS $body$
BEGIN

