					nm_usuario_p => nm_usuario_p,
					ie_commit_p => 'S');
elsif (ie_acao_p = 'E') then	
	CALL pls_chamado_pck.encerrar_chamado(	nr_seq_chamado_etapa_p => nr_seq_chamado_etapa_p,
					nr_seq_motivo_encerramento_p => nr_seq_motivo_encerramento_p,
					nm_usuario_p => nm_usuario_p);
elsif (ie_acao_p = 'D') then
	CALL pls_chamado_pck.desfazer_liberacao_hist(nr_seq_historico_p => nr_seq_historico_p, nm_usuario_p => nm_usuario_p);
end if;

