CREATE OR REPLACE PROCEDURE pls_checar_titulo_desc_folha ( nr_seq_titulo_cobr_p bigint, nr_seq_lote_cobr_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_verificacao_w	varchar(1);
nr_seq_cobr_w		titulo_receber_cobr.nr_sequencia%type;
qt_desmarcado_w		integer;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	titulo_receber_cobr
	where	nr_seq_cobranca = nr_seq_lote_cobr_p;


BEGIN

if (nr_seq_titulo_cobr_p > 0) then
	select	coalesce(ie_verificacao,'N')
	into STRICT	ie_verificacao_w
	from	titulo_receber_cobr
	where	nr_sequencia	= nr_seq_titulo_cobr_p;

	if (ie_verificacao_w = 'S') then
		update	titulo_receber_cobr
		set	ie_verificacao	= 'N'
		where	nr_sequencia	= nr_seq_titulo_cobr_p;
	elsif (ie_verificacao_w = 'N') then
		update	titulo_receber_cobr
		set	ie_verificacao	= 'S'
		where	nr_sequencia	= nr_seq_titulo_cobr_p;
	end if;
elsif (nr_seq_lote_cobr_p > 0) then
	select	count(1)
	into STRICT	qt_desmarcado_w
	from	titulo_receber_cobr
	where	nr_seq_cobranca	= nr_seq_lote_cobr_p
	and	coalesce(ie_verificacao,'N') = 'N';

	open C01;
	loop
	fetch C01 into
		nr_seq_cobr_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (qt_desmarcado_w > 0) then
			update	titulo_receber_cobr
			set	ie_verificacao	= 'S'
			where	nr_sequencia	= nr_seq_cobr_w;
		else
			update	titulo_receber_cobr
			set	ie_verificacao	= 'N'
			where	nr_sequencia	= nr_seq_cobr_w;
		end if;
		end;
	end loop;
	close C01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
