CREATE OR REPLACE PROCEDURE pls_click_botao_simul_coletivo ( nr_seq_simul_perfil_p bigint, ds_Tab_Selecionadas_p text, ds_Tab_Nao_Selec_p text, nm_usuario_p text) AS $body$
DECLARE


dsTabelasSelecionadas_w		varchar(255);
dsTabelasNaoSelec_w		varchar(255);
nr_seq_tabela_w			bigint;


BEGIN

dsTabelasSelecionadas_w		:= ds_Tab_Selecionadas_p;
dsTabelasNaoSelec_w		:= ds_Tab_Nao_Selec_p;

delete	FROM pls_simulacao_plano
where	nr_seq_simul_perfil	= nr_seq_simul_perfil_p;

/*Gera os dados da tabela selecionada*/

while(dsTabelasSelecionadas_w IS NOT NULL AND dsTabelasSelecionadas_w::text <> '') loop
	nr_seq_tabela_w		:= substr(dsTabelasSelecionadas_w, 1, position(',' in dsTabelasSelecionadas_w) - 1);
	dsTabelasSelecionadas_w	:= substr(dsTabelasSelecionadas_w, position(',' in dsTabelasSelecionadas_w) + 1, length(dsTabelasSelecionadas_w));

	CALL pls_gerar_dados_simul_plano(nr_seq_simul_perfil_p,nr_seq_tabela_w,nm_usuario_p);
	CALL pls_sel_tab_simul_coletivo(nr_seq_simul_perfil_p,nr_seq_tabela_w,'S',nm_usuario_p);
end loop;

/*Marca os dados das tabelas n√£o selecionadas*/

while(dsTabelasNaoSelec_w IS NOT NULL AND dsTabelasNaoSelec_w::text <> '') loop
	nr_seq_tabela_w		:= substr(dsTabelasNaoSelec_w, 1, position(',' in dsTabelasNaoSelec_w) - 1);
	dsTabelasNaoSelec_w	:= substr(dsTabelasNaoSelec_w, position(',' in dsTabelasNaoSelec_w) + 1, length(dsTabelasNaoSelec_w));

	CALL pls_sel_tab_simul_coletivo(nr_seq_simul_perfil_p,nr_seq_tabela_w,'N',nm_usuario_p);
end loop;

commit;

end;
$body$
