CREATE OR REPLACE PROCEDURE pls_confirmar_envio_cpm ( nr_seq_lote_p bigint, ie_opcao_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Confirmar e desfazer o envio de XML do CPM
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
	ie_opcao_p:
		C - Confirmar
		D - Desfazer
-------------------------------------------------------------------------------------------------------------------
Referências:
	OPS - Envio de Informações Cadastrais de Prestadores Médicos (CPM)
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

if (ie_opcao_p = 'C') then
	update	pls_lote_cpm
	set	dt_envio	= clock_timestamp()
	where	nr_sequencia	= nr_seq_lote_p;
else
	update	pls_lote_cpm
	set	dt_envio	 = NULL
	where	nr_sequencia	= nr_seq_lote_p;
end if;

commit;

end;
$body$
