	update	ptu_fatura_geral
	set	dt_postagem		= dt_envio_arquivo_p,
		nm_usuario_postagem	= nm_usuario_p,
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_fat_geral_p;
end if;

commit;

end;
