CREATE OR REPLACE PROCEDURE pls_confirmar_envio_rpc ( nr_seq_lote_p bigint, ie_desfazer_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_desfazer_p = 'S') then
	update	pls_rpc_lote
	set	dt_envio	 = NULL,
		nm_usuario_envio  = NULL
	where	nr_sequencia	= nr_seq_lote_p;
else
	update	pls_rpc_lote
	set	dt_envio	= clock_timestamp(),
		nm_usuario_envio = nm_usuario_p
	where	nr_sequencia	= nr_seq_lote_p;
end if;

commit;

end;
