CREATE OR REPLACE PROCEDURE pls_confirmar_fim_aditamento ( nr_seq_aditamento_p bigint, dt_fim_aditamento_p timestamp, nm_usuario_p text) AS $body$
DECLARE


dt_aditamento_w		timestamp;


BEGIN

select 	trunc(dt_aditamento)
into STRICT	dt_aditamento_w
from	pls_contrato_aditamento
where	nr_sequencia	= nr_seq_aditamento_p;

if (trunc(dt_fim_aditamento_p,'dd') >= trunc(dt_aditamento_w,'dd')) then
	update	pls_contrato_aditamento
	set	dt_fim_aditamento	= dt_fim_aditamento_p,
		nm_usuario_confirmacao	= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_aditamento_p;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(451332, null);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
