CREATE OR REPLACE PROCEDURE pls_confirmar_lote_ir ( nr_seq_lote_p bigint, ie_confirmar_desfazer_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_confirmar_desfazer_p = 'C') then
	update	pls_lote_mens_ir
	set	dt_confirmacao		= clock_timestamp(),
		nm_usuario_confirmacao	= nm_usuario_p
	where	nr_sequencia		= nr_seq_lote_p;
elsif (ie_confirmar_desfazer_p = 'D') then
	update	pls_lote_mens_ir
	set	dt_confirmacao		 = NULL,
		nm_usuario_confirmacao	 = NULL
	where	nr_sequencia		= nr_seq_lote_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
