CREATE OR REPLACE PROCEDURE pls_confirma_envio_notific ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: Marcar o lote de notificações como enviado 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ X ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
------------------------------------------------------------------------------------------------------------------- 
Referências: 
	OPS - Controle de notificações de atraso 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
tb_nr_seq_pagador_w		pls_util_cta_pck.t_number_table;

C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_notificacao_pagador 
	where	nr_seq_lote	= nr_seq_lote_p 
	and (ie_status <> 'R' or coalesce(ie_status::text, '') = '');
	

BEGIN 
update	pls_notificacao_pagador 
set	ie_status_ant	= ie_status, 
	ie_status	= 'E', 
	nm_usuario	= nm_usuario_p 
where	nr_seq_lote	= nr_seq_lote_p 
and (ie_status <> 'R' or coalesce(ie_status::text, '') = '');
 
update	pls_notificacao_lote 
set	dt_envio	= clock_timestamp(), 
	nm_usuario	= nm_usuario_p 
where	nr_sequencia	= nr_seq_lote_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
