SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_confirm_desf_postagem_arq ( nr_seq_lote_p bigint, nr_seq_fatura_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
	ie_opcao_p
	CL = Confirmar envio/postagem dos arquivos do lote
	CF = Confirmar envio/postagem do arquivo da fatura
	DL = Desfazer confirmação de envio/postagem dos arquivos do lote
	DF = Desfazer confirmação de envio/postagem do arquivo da fatura
*/
BEGIN
if (trim(both ie_opcao_p) = 'CL') then
	update	pls_fatura
	set	dt_postagem_arquivo	= clock_timestamp(),
		nm_usuario_envio 	= nm_usuario_p
	where	nr_seq_lote		= nr_seq_lote_p
	and	coalesce(dt_postagem_arquivo::text, '') = ''
	and	coalesce(ie_cancelamento,'X') not in ('C','E')
	and	coalesce(ie_impedimento_cobranca,'X') not in ('NF','BP');

elsif (trim(both ie_opcao_p) = 'CF') then
	update	pls_fatura
	set	dt_postagem_arquivo	= clock_timestamp(),
		nm_usuario_envio 	= nm_usuario_p
	where	nr_sequencia		= nr_seq_fatura_p;

elsif (trim(both ie_opcao_p) = 'DL') then
	update	pls_fatura
	set	dt_postagem_arquivo	 = NULL,
		nm_usuario_envio 	 = NULL
	where	nr_seq_lote		= nr_seq_lote_p
	and	(dt_postagem_arquivo IS NOT NULL AND dt_postagem_arquivo::text <> '')
	and	coalesce(ie_cancelamento,'X') not in ('C','E')
	and	coalesce(ie_impedimento_cobranca,'X') not in ('NF','BP');

elsif (trim(both ie_opcao_p) = 'DF') then
	update	pls_fatura
	set	dt_postagem_arquivo	 = NULL,
		nm_usuario_envio 	 = NULL
	where	nr_sequencia		= nr_seq_fatura_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
