CREATE OR REPLACE PROCEDURE pls_conf_receb_carteira_web ( nr_seq_carteira_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Atualizar a solicitação da carteira do beneficiário, confirmando que o mesmo recebeu a carteirinha
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [ x] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Esta rotina é utilizado no acesso do Beneficiário e do Estipulante
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_registros_w		bigint;



BEGIN

select 	count(1)
into STRICT	qt_registros_w
from 	pls_carteira_emissao
where 	nr_seq_seg_carteira = nr_seq_carteira_p;

if (qt_registros_w > 0) then

	update 	pls_carteira_emissao
	set    	dt_entrega_beneficiario = clock_timestamp(),
		nm_usuario_entrega 	= nm_usuario_p
        where  	nr_seq_seg_carteira 	= nr_seq_carteira_p;

	/* Rotina utilizada para atualizar o estágio da emissão da carteirinha */

	CALL pls_alterar_estagios_cartao(nr_seq_carteira_p, clock_timestamp(), 7, cd_estabelecimento_p, nm_usuario_p);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
