CREATE OR REPLACE PROCEDURE pls_consiste_carteira_benef ( nr_seq_segurado_p bigint, nr_seq_carteira_p bigint, cd_usuario_plano_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_usuario_plano_w	pls_segurado_carteira.cd_usuario_plano%type;
count_w			integer;


BEGIN

select	count(1)
into STRICT	count_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p
and	ie_tipo_segurado = 'P';

if (count_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(262193);
	/* Mensagem: Não é permitido cadastrar informações da carteira de identificação, a mesma é gerada automaticamente ao liberar o usuário. */

end if;

select	count(1)
into STRICT	count_w
from	pls_segurado_carteira
where	nr_seq_segurado	= nr_seq_segurado_p;

if (count_w > 1) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(262194);
	/* Mensagem: Não é permitida a inclusão de mais de uma carteira para o beneficiário. */

end if;

select	coalesce(max(cd_usuario_plano),'0')
into STRICT	cd_usuario_plano_w
from	pls_segurado_carteira
where	cd_usuario_plano = cd_usuario_plano_p
and	nr_seq_segurado <> nr_seq_segurado_p;

if (cd_usuario_plano_w <> '0') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(262195);
	/* Mensagem: Esta carteira já pertence a outro beneficiário! Verifique. */

end if;

end;
$body$
