SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consiste_contrato_plano ( nr_seq_contrato_p bigint, nr_seq_plano_p bigint, nr_seq_tabela_p bigint, nr_seq_contrato_plano_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ie_tipo_contratacao_w		pls_plano.ie_tipo_contratacao%type;
nr_seq_plano_w			pls_plano.nr_sequencia%type;
ie_tipo_operacao_w		pls_plano.ie_tipo_operacao%type;
ie_situacao_w			pls_plano.ie_situacao%type;
nr_contrato_w			pls_tabela_preco.nr_contrato%type;
ie_tabela_base_w		pls_tabela_preco.ie_tabela_base%type;
ie_permite_prod_dif_w		pls_contrato.ie_permite_prod_dif%type;
ie_tipo_operacao_contr_w	pls_contrato.ie_tipo_operacao%type;
qt_produtos_w			bigint;
ie_tipo_estipulante_w		varchar(1);
ds_erro_w			varchar(4000)	:= '';
dt_contrato_w			pls_contrato.dt_contrato%type;
dt_inicio_vigencia_w		pls_tabela_preco.dt_inicio_vigencia%type;
dt_fim_vigencia_w		pls_tabela_preco.dt_fim_vigencia%type;
ie_empresario_individual_w      pls_contrato.ie_empresario_individual%type;


BEGIN

if (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then
	select	CASE WHEN coalesce(cd_pf_estipulante::text, '') = '' THEN 'J'  ELSE 'F' END ,
		coalesce(ie_permite_prod_dif,'N'),
		ie_tipo_operacao,
		dt_contrato,
		coalesce(ie_empresario_individual, 'N')
	into STRICT	ie_tipo_estipulante_w,
		ie_permite_prod_dif_w,
		ie_tipo_operacao_contr_w,
		dt_contrato_w,
		ie_empresario_individual_w
	from	pls_contrato
	where	nr_sequencia	= nr_seq_contrato_p;
	
	select	count(1)
	into STRICT	qt_produtos_w
	from	pls_contrato_plano
	where	nr_seq_contrato	= nr_seq_contrato_p
	and	nr_sequencia	<> nr_seq_contrato_plano_p
	and	ie_situacao	= 'A'
	and	nr_seq_tabela	= nr_seq_tabela_p;
	
	if (ie_permite_prod_dif_w = 'N') and (qt_produtos_w > 0) then
		ds_erro_w	:= wheb_mensagem_pck.get_texto(278197); --Não é possível inserir mais de um produto para o contrato. Verifique!
	end if;
	
	if (nr_seq_plano_p IS NOT NULL AND nr_seq_plano_p::text <> '') then
		select	count(1)
		into STRICT	qt_produtos_w
		from	pls_contrato_plano
		where	nr_seq_plano	= nr_seq_plano_p
		and	nr_seq_contrato	= nr_seq_contrato_p
		and	nr_sequencia	<> nr_seq_contrato_plano_p
		and	ie_situacao	= 'A';
		
		if (qt_produtos_w > 0) then
			ds_erro_w	:= wheb_mensagem_pck.get_texto(278199); --Produto já cadastrado para o contrato. Verifique!
		end if;
		
		select	ie_tipo_contratacao,
			ie_tipo_operacao,
			ie_situacao
		into STRICT	ie_tipo_contratacao_w,
			ie_tipo_operacao_w,
			ie_situacao_w
		from	pls_plano
		where	nr_sequencia	= nr_seq_plano_p;
		
		if (ie_situacao_w = 'S') then
			ds_erro_w	:= wheb_mensagem_pck.get_texto(347837); --O produto selecionado está com a comercialização suspensa. Favor verifique.
		elsif (ie_situacao_w = 'I') then
			ds_erro_w	:= wheb_mensagem_pck.get_texto(347838); --O produto selecionado está cancelado. Favor verifique.
		end if;
		
		if (ie_tipo_operacao_contr_w <> ie_tipo_operacao_w) then
			ds_erro_w	:= wheb_mensagem_pck.get_texto(278200); --Tipo de operação do produto diferente do tipo de operação do contrato. Verifique!
		end if;
		
		if (ie_tipo_estipulante_w = 'F') and (ie_tipo_contratacao_w <> 'I') and (ie_empresario_individual_w = 'N') then
			ds_erro_w	:= wheb_mensagem_pck.get_texto(278201); --Produto selecionado é Coletivo, não sendo permitido para contratos de pessoa física!
		elsif (ie_tipo_estipulante_w = 'J') and (ie_tipo_contratacao_w = 'I') then
			ds_erro_w	:= wheb_mensagem_pck.get_texto(278210); --Produto selecionado é Individual/Familiar, não sendo permitido para contratos de pessoa jurídica!
		end if;
		
		select	count(1)
		into STRICT	qt_produtos_w
		from	pls_contrato_plano a,
			pls_plano b
		where	a.nr_seq_plano		= b.nr_sequencia
		and	a.nr_seq_contrato	= nr_seq_contrato_p
		and	b.ie_tipo_contratacao <> ie_tipo_contratacao_w
		and	a.ie_situacao	    = 'A';
		
		if (qt_produtos_w > 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(340674); --Não é possível incluir produtos com tipo de contratação diferente para o contrato. Favor verifique.
		end if;
		
		if (nr_seq_tabela_p IS NOT NULL AND nr_seq_tabela_p::text <> '') then
			select	max(nr_seq_plano),
				max(nr_contrato),
				max(ie_tabela_base),
				max(dt_inicio_vigencia),
				max(dt_fim_vigencia)
			into STRICT	nr_seq_plano_w,
				nr_contrato_w,
				ie_tabela_base_w,
				dt_inicio_vigencia_w,
				dt_fim_vigencia_w
			from	pls_tabela_preco
			where	nr_sequencia	= nr_seq_tabela_p;
			
			if (nr_seq_plano_w <> nr_seq_plano_p) then
				ds_erro_w	:= wheb_mensagem_pck.get_texto(278202); --A tabela selecionada não pertence ao produto!
			end if;
			
			if (dt_contrato_w > dt_fim_vigencia_w) then
				ds_erro_w	:= wheb_mensagem_pck.get_texto(671663); --A tabela de preço não está liberada para comercialização na data do contrato.
			end if;
			
			if (ie_tabela_base_w = 'S') then
				ds_erro_w	:= wheb_mensagem_pck.get_texto(278203); --Não é permitida a utilização de tabelas bases por percentual para os produtos. As mesmas são utilizadas para a geração de novas tabelas para o beneficiário!
			end if;
			
			if (nr_contrato_w IS NOT NULL AND nr_contrato_w::text <> '') and (nr_contrato_w <> nr_seq_contrato_p) and (ie_tipo_contratacao_w <> 'I') then
				ds_erro_w	:= wheb_mensagem_pck.get_texto(278204); --A tabela selecionada pertence a outro contrato!
			end if;
		end if;	
	else
		ds_erro_w	:= wheb_mensagem_pck.get_texto(278206); --Plano não informado!
	end if;
else
	ds_erro_w	:= wheb_mensagem_pck.get_texto(278208); --Contrato não informado!
end if;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_consiste_contrato_plano ( nr_seq_contrato_p bigint, nr_seq_plano_p bigint, nr_seq_tabela_p bigint, nr_seq_contrato_plano_p bigint, ds_erro_p INOUT text) FROM PUBLIC;

