CREATE OR REPLACE PROCEDURE pls_consiste_data_cta ( dt_protocolo_p timestamp, dt_item_p timestamp, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE

/*Procedure criado com o intuíto de verificar de as datas informadas são superiores a data do protocolo, caso forem sera barrado o processo, procedure utilizada na função OPS - Gestão de conta médicas e
OPS - Controle de reembolso*/
BEGIN

if (trunc(dt_protocolo_p) < trunc(dt_item_p)) then
	ds_retorno_p := 'Data superior a data do protocolo';
end if;

end;
$body$
