CREATE OR REPLACE PROCEDURE pls_consiste_data_lote_mens_js ( nr_sequencia_p bigint, ie_status_p bigint, ie_novo_lote_aberto_p text, dt_mesano_ref_p timestamp, dt_contabil_p timestamp, dt_mesano_referencia_p timestamp, ie_opcao_p text, cd_estabelecimento_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE


qt_lote_aberto_w	bigint;
ie_mes_fechado_w	varchar(2);


BEGIN

select	count(*)
into STRICT	qt_lote_aberto_w
from	pls_lote_mensalidade
where	nr_sequencia	<> nr_sequencia_p
and	ie_status		<> ie_status_p;

if (qt_lote_aberto_w > 0
	and	ie_novo_lote_aberto_p = 'N') then
		ds_retorno_p	:= substr(obter_texto_tasy(38892, wheb_usuario_pck.get_nr_seq_idioma),1,255);
else
	begin
	ds_retorno_p := pls_consistir_data_lote_mens(
		dt_mesano_ref_p, dt_contabil_p, ds_retorno_p, cd_estabelecimento_p, wheb_usuario_pck.get_nm_usuario);--Coloquei os campos estab e usu√°rio (Geliard)
	if (coalesce(ds_retorno_p::text, '') = '') then
		begin
		select	substr(pls_obter_se_mes_fechado(
				coalesce(dt_contabil_p,dt_mesano_referencia_p),
				ie_opcao_p,
				cd_estabelecimento_p), 1, 2)
		into STRICT	ie_mes_fechado_w
		;

		if (ie_mes_fechado_w = 'S') then
			ds_retorno_p	:= substr(obter_texto_tasy(38893, wheb_usuario_pck.get_nr_seq_idioma),1,255);
		end if;
		end;
	end if;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
