SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consiste_data_sib (dt_referencia_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


count_w			bigint;
vl_parametro_w		varchar(1);


BEGIN

vl_parametro_w := Obter_Param_Usuario(1202, 16, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, vl_parametro_w);

if (coalesce(vl_parametro_w,'N') = 'S') then
	select	count(1)
	into STRICT	count_w
	from	pls_lote_sib
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	ie_tipo_envio		= 'E'
	and	(trunc(dt_referencia_p,'month') < trunc(dt_mesano_referencia,'month')
	or	trunc(dt_referencia_p,'month') <
				(SELECT	max(trunc(dt_mesano_referencia,'month'))
				from	pls_lote_sib
				where	cd_estabelecimento = cd_estabelecimento_p));

	if (count_w = 0) then
		select	count(1)
		into STRICT	count_w
		from	pls_sib_lote
		where	cd_estabelecimento	= cd_estabelecimento_p
		and	ie_tipo_lote		= 'M'
		and	ie_status		<> 1
		and	dt_referencia_p between trunc(dt_inicio_mov,'dd') and fim_dia(dt_fim_mov);
	end if;

	if (count_w > 0) then
		-- Já foi enviado um lote para o SIB no mês #@DT_REFERENCIA#@.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266786, 'DT_REFERENCIA=' || to_char(dt_referencia_p,'mm/yyyy'));
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
