CREATE OR REPLACE PROCEDURE pls_consiste_diops_trans_conta ( nr_sequencia_p bigint, nr_seq_transacao_p bigint, ie_tipo_conta_p text, ds_conta_p text, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	diops_trans_conta
where	nr_seq_transacao	= nr_seq_transacao_p
and	ie_tipo_conta 		= ie_tipo_conta_p
and	ds_conta 		= ds_conta_p
and	nr_sequencia		<> nr_sequencia_p;

if (qt_registros_w > 0 ) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(451158);
	--'Já existe outro cadastro com o mesmo código ANS para mesma transação e regra.'
end if;

end;
