CREATE OR REPLACE PROCEDURE pls_consiste_dt_competencia ( dt_competencia_p timestamp, dt_alta_p timestamp, ie_tipo_p text, nr_seq_protocolo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/*
ie_tipo_p
c - conta
p - procedimento
m - material
*/
ie_tipo_w		varchar(50);
dt_protocolo_w		timestamp;
dt_emissao_w		timestamp;
dt_competencia_w	timestamp;
dt_alta_w		timestamp;


BEGIN
/*os 286822*/

dt_competencia_w := dt_competencia_p; --to_date(dt_competencia_p,'dd/mm/yyyy hh24:mi:ss');
dt_alta_w := dt_alta_p;

if (coalesce(obter_valor_param_usuario(1208, 39, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p), 'S') = 'N') then

	begin
	select	trunc(a.dt_protocolo)
	into STRICT	dt_protocolo_w
	from	pls_protocolo_conta a
	where	a.nr_sequencia = nr_seq_protocolo_p;
	exception
	when others then
		dt_protocolo_w := null;
	end;

	if (ie_tipo_p = 'C') then
		if (coalesce(dt_alta_p::text, '') = '') then
			if	((trunc(dt_competencia_w) > trunc(dt_protocolo_w)) and (dt_protocolo_w IS NOT NULL AND dt_protocolo_w::text <> '') 	and (dt_competencia_w IS NOT NULL AND dt_competencia_w::text <> '')) 	then
				CALL wheb_mensagem_pck.exibir_mensagem_abort(267417);
			end if;
		else
			if	((trunc(dt_alta_w) > trunc(dt_protocolo_w)) and (dt_protocolo_w IS NOT NULL AND dt_protocolo_w::text <> '') 	and (dt_alta_w IS NOT NULL AND dt_alta_w::text <> '')) 	then
				CALL wheb_mensagem_pck.exibir_mensagem_abort(267418);
			end if;
		end if;

	end if;
end if;

select	CASE WHEN ie_tipo_p='C' THEN  'a conta' WHEN ie_tipo_p='P' THEN  'o procedimento' WHEN ie_tipo_p='M' THEN  'o material' END
into STRICT	ie_tipo_w
;

if (trunc(dt_competencia_w) > trunc(clock_timestamp())) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(267419,'IE_TIPO_W=' || ie_tipo_w);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
