CREATE OR REPLACE PROCEDURE pls_consiste_faixa_tit_tabela ( nr_seq_tabela_p pls_plano_preco.nr_seq_tabela%type ) AS $body$
DECLARE


qt_tipo_titularidade_w		bigint;

c01 CURSOR FOR
	SELECT	qt_idade_inicial,
		qt_idade_final,
		ie_grau_titularidade
	from	pls_plano_preco
	where	nr_seq_tabela = nr_seq_tabela_p;

BEGIN

--Verifica se existem faixa et√°rias diferentes para cada grau de titularidade cadastrado
for r_c01_w in c01 loop
	begin
	
	select	count(1)
	into STRICT	qt_tipo_titularidade_w
	from	pls_plano_preco
	where	nr_seq_tabela = nr_seq_tabela_p
	and	coalesce(ie_grau_titularidade,'X') <> coalesce(r_c01_w.ie_grau_titularidade,'X');
	
	if (qt_tipo_titularidade_w > 0) then
		select	count(1)
		into STRICT	qt_tipo_titularidade_w
		from	pls_plano_preco
		where	nr_seq_tabela = nr_seq_tabela_p
		and	coalesce(ie_grau_titularidade,'X') <> coalesce(r_c01_w.ie_grau_titularidade,'X')
		and	qt_idade_inicial = r_c01_w.qt_idade_inicial
		and	qt_idade_final = r_c01_w.qt_idade_final;
		
		if (qt_tipo_titularidade_w = 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1064470,'NR_SEQ_TABELA='||nr_seq_tabela_p);
		end if;
	end if;
	end;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
