SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consiste_inclusao_benef ( nr_seq_contrato_p bigint, nr_seq_segurado_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_limite_movimentacao_w	integer;
ie_acao_contrato_w		varchar(10);
dt_dia_inclusao_w		integer;
ie_tipo_data_w			varchar(10);
ie_lanca_regra_w		varchar(2) := 'N';
ie_verifica_regra_w		varchar(2) := 'N';
nr_seq_contrato_w		bigint;
nr_seq_segurado_w		bigint;
dt_contratacao_w		timestamp;
dt_contrato_w			timestamp;
cd_estabelecimento_w		smallint;
nr_seq_regra_incl_benef_w	bigint;
ie_estipulante_contrato_w	varchar(10);
ie_tipo_contratacao_w		varchar(2);
dt_contrat_add_w		timestamp;
ie_aplicacao_regra_w		varchar(2);
ie_processo_w			varchar(2);
ie_manter_data_inclusao_w	varchar(1);

C01 CURSOR FOR
	SELECT	a.ie_acao_contrato,
		a.nr_seq_contrato,
		a.nr_sequencia,
		a.dt_contratacao,
		b.ie_tipo_contratacao
	from	pls_segurado	a,
		pls_plano	b
	where	a.nr_seq_plano		= b.nr_sequencia
	and	((a.nr_sequencia 	= nr_seq_segurado_p) or (a.nr_seq_contrato	= nr_seq_contrato_p));
		
C02 CURSOR FOR
	SELECT	nr_sequencia
	from	(	SELECT	a.nr_sequencia,
				a.ie_acao_contrato,
				a.ie_estipulante_contrato,
				a.dt_limite_movimentacao
			from	pls_regra_inclusao_benef	a,
				pls_contrato			b
			where	a.nr_seq_contrato	= b.nr_sequencia
			and	a.nr_seq_contrato 	= nr_seq_contrato_w
			and	clock_timestamp() between a.dt_inicio_vigencia and coalesce(a.dt_fim_vigencia,clock_timestamp())
			and	((coalesce(a.ie_estipulante_contrato,'A') = ie_estipulante_contrato_w) or (coalesce(a.ie_estipulante_contrato,'A') = 'A'))
			and	coalesce(a.ie_acao_contrato,ie_acao_contrato_w)	= ie_acao_contrato_w
			and	coalesce(a.ie_aplicacao_regra,'T') in ('T','M')
			and	(to_char(dt_contratacao_w,'dd'))::numeric  <= a.dt_limite_movimentacao
			
union all

			select	a.nr_sequencia,
				a.ie_acao_contrato,
				a.ie_estipulante_contrato,
				a.dt_limite_movimentacao
			from	pls_regra_inclusao_benef	a
			where	coalesce(a.nr_seq_contrato::text, '') = ''
			and	a.cd_estabelecimento	= cd_estabelecimento_w
			and	clock_timestamp() between a.dt_inicio_vigencia and coalesce(a.dt_fim_vigencia,clock_timestamp())
			and	((coalesce(a.ie_estipulante_contrato,'A') = ie_estipulante_contrato_w) or (coalesce(a.ie_estipulante_contrato,'A') = 'A'))
			and	coalesce(a.ie_acao_contrato,ie_acao_contrato_w)	= ie_acao_contrato_w
			and	((a.ie_tipo_contratacao = ie_tipo_contratacao_w) or (coalesce(a.ie_tipo_contratacao::text, '') = ''))
			and	(to_char(dt_contratacao_w,'dd'))::numeric 	<= a.dt_limite_movimentacao
			and	coalesce(a.ie_aplicacao_regra,'T') in ('T','M')
			and	not exists (	select	1
							from	pls_regra_inclusao_benef	x,
								pls_contrato			y
							where	x.nr_seq_contrato	= y.nr_sequencia
							and	x.nr_seq_contrato 	= nr_seq_contrato_w)) alias32
	order by coalesce(ie_acao_contrato,'-1'),
		 coalesce(ie_estipulante_contrato,'-1'),
		 dt_limite_movimentacao;


BEGIN

open C01;
loop
fetch C01 into
	ie_acao_contrato_w,
	nr_seq_contrato_w,
	nr_seq_segurado_w,
	dt_contratacao_w,
	ie_tipo_contratacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	cd_estabelecimento,
		dt_contrato,
		CASE WHEN coalesce(cd_cgc_estipulante::text, '') = '' THEN 'PF' WHEN cd_cgc_estipulante=cd_pf_estipulante THEN null  ELSE 'PJ' END
	into STRICT	cd_estabelecimento_w,
		dt_contrato_w,
		ie_estipulante_contrato_w
	from	pls_contrato
	where	nr_sequencia	= nr_seq_contrato_w;
		
	ie_verifica_regra_w	:= 'S';
	ie_lanca_regra_w 	:= 'N';
	
	open C02;
	loop
	fetch C02 into
		nr_seq_regra_incl_benef_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		
		if (ie_verifica_regra_w = 'S') then
			select	a.dt_dia_inclusao,
				a.ie_tipo_data,
				coalesce(a.dt_limite_movimentacao,30),
				coalesce(ie_aplicacao_regra, 'T'),
				coalesce(ie_manter_data_inclusao,'N')
			into STRICT	dt_dia_inclusao_w,
				ie_tipo_data_w,
				dt_limite_movimentacao_w,
				ie_aplicacao_regra_w,
				ie_manter_data_inclusao_w
			from	pls_regra_inclusao_benef	a
			where	a.nr_sequencia	= nr_seq_regra_incl_benef_w;
			
			if ((to_char(dt_contratacao_w,'dd'))::numeric  <= dt_limite_movimentacao_w) then
				ie_lanca_regra_w 	:= 'S';
				ie_verifica_regra_w	:= 'N';
			end if;
		end if;
		
		if (ie_manter_data_inclusao_w = 'S') then
			ie_lanca_regra_w	:= 'N';
		elsif (ie_aplicacao_regra_w = 'T') then
			select	max(b.ie_processo)
			into STRICT	ie_processo_w
			from	pls_inclusao_beneficiario	b,
				pls_segurado			a
			where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
			and	a.nr_sequencia = nr_seq_segurado_w;
						
			if (coalesce(ie_processo_w,'T') = 'W') then
				ie_lanca_regra_w 	:= 'N';
			else
				ie_lanca_regra_w 	:= 'S';
			end if;
		else
			ie_lanca_regra_w 	:= 'S';
		end if;	

		if (ie_verifica_regra_w = 'N') then
			exit;			
		end if;
	
		end;
	end loop;
	close C02;
	
	if (ie_lanca_regra_w <> 'S') then
		ie_lanca_regra_w := 'N';
	end if;
	
	if (ie_lanca_regra_w = 'S') then			
		if (ie_tipo_data_w = 'P') then
			begin			
			select	add_months(trunc(dt_contratacao,'mm')-1 +(dt_dia_inclusao_w),+1)
			into STRICT	dt_contrat_add_w
			from	pls_segurado
			where	nr_sequencia = nr_seq_segurado_w;
			
			update	pls_segurado
			set	dt_contratacao 		= dt_contrat_add_w,
				dt_inclusao_operadora	= add_months(trunc(dt_inclusao_operadora,'mm')-1 +(dt_dia_inclusao_w),+1)
			where	nr_sequencia		= nr_seq_segurado_w;
			
			update	pls_segurado_carteira
			set	dt_inicio_vigencia	= add_months(trunc(dt_inicio_vigencia,'mm')-1 +(dt_dia_inclusao_w),+1)
			where	nr_seq_segurado		= nr_seq_segurado_w;
			
			update	pls_sca_vinculo
			set	dt_inicio_vigencia = dt_contrat_add_w
			where	nr_seq_segurado = nr_seq_segurado_w
			and	dt_inicio_vigencia < dt_contrat_add_w;
			end;
		elsif (ie_tipo_data_w = 'SP') then
			begin
			select	add_months(trunc(dt_contratacao,'mm')-1 +(dt_dia_inclusao_w),+2)
			into STRICT	dt_contrat_add_w
			from	pls_segurado
			where	nr_sequencia = nr_seq_segurado_w;		
			
			update	pls_segurado
			set	dt_contratacao 		= dt_contrat_add_w,
				dt_inclusao_operadora	= add_months(trunc(dt_inclusao_operadora,'mm')-1 +(dt_dia_inclusao_w),+2)
			where	nr_sequencia		= nr_seq_segurado_w;
			
			update	pls_segurado_carteira
			set	dt_inicio_vigencia	= add_months(trunc(dt_inicio_vigencia,'mm')-1 +(dt_dia_inclusao_w),+2)
			where	nr_seq_segurado		= nr_seq_segurado_w;
			
			update	pls_sca_vinculo
			set	dt_inicio_vigencia	= dt_contrat_add_w
			where	nr_seq_segurado		= nr_seq_segurado_w
			and	dt_inicio_vigencia	< dt_contrat_add_w;
			end;
		elsif (ie_tipo_data_w = 'A') then
			begin
			select	trunc(dt_contratacao,'mm')-1 +(dt_dia_inclusao_w)
			into STRICT	dt_contrat_add_w
			from	pls_segurado
			where	nr_sequencia = nr_seq_segurado_w;
			
			update	pls_segurado
			set	dt_contratacao 		= dt_contrat_add_w,
				dt_inclusao_operadora	= trunc(dt_inclusao_operadora,'mm')-1 +(dt_dia_inclusao_w)
			where	nr_sequencia		= nr_seq_segurado_w;
			
			update	pls_segurado_carteira
			set	dt_inicio_vigencia	= trunc(dt_inicio_vigencia,'mm')-1 +(dt_dia_inclusao_w)
			where	nr_seq_segurado		= nr_seq_segurado_w;
			
			update	pls_sca_vinculo
			set	dt_inicio_vigencia = dt_contrat_add_w
			where	nr_seq_segurado = nr_seq_segurado_w
			and	dt_inicio_vigencia < dt_contrat_add_w;
			end;
		end if;
	end if;
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_consiste_inclusao_benef ( nr_seq_contrato_p bigint, nr_seq_segurado_p bigint, nm_usuario_p text) FROM PUBLIC;

