SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consiste_intercambio_plano ( nr_seq_intercambio_p bigint, nr_seq_plano_p bigint, nr_seq_tabela_p bigint, nr_seq_interc_plano_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


nr_seq_congenere_w		bigint;
nr_seq_oper_congenere_w		bigint;
ie_tipo_estipulante_w		varchar(1);
ie_tipo_contratacao_w		varchar(3);
nr_seq_plano_w			bigint;
cont_w				bigint;
dt_inicio_vigencia_w		timestamp;
dt_inclusao_w			timestamp;
ds_erro_w			varchar(4000)	:= '';
ie_tipo_operacao_w		varchar(3);
ie_tipo_operacao_int_w		varchar(3);
ie_preco_w			varchar(10);


BEGIN

if (nr_seq_intercambio_p IS NOT NULL AND nr_seq_intercambio_p::text <> '') then
	select	CASE WHEN coalesce(cd_pessoa_fisica::text, '') = '' THEN 'J'  ELSE 'F' END ,
		trunc(dt_inclusao)
	into STRICT	ie_tipo_estipulante_w,
		dt_inclusao_w
	from	pls_intercambio
	where	nr_sequencia	= nr_seq_intercambio_p;

	select	nr_seq_congenere,
		nr_seq_oper_congenere
	into STRICT	nr_seq_congenere_w,
		nr_seq_oper_congenere_w
	from	pls_intercambio
	where	nr_sequencia	= nr_seq_intercambio_p;

	if (nr_seq_congenere_w IS NOT NULL AND nr_seq_congenere_w::text <> '') then
		ie_tipo_operacao_int_w	:= 'T';
	elsif (nr_seq_oper_congenere_w IS NOT NULL AND nr_seq_oper_congenere_w::text <> '') then
		ie_tipo_operacao_int_w	:= 'C';
	end if;

	if (nr_seq_plano_p IS NOT NULL AND nr_seq_plano_p::text <> '') then
		select	count(*)
		into STRICT	cont_w
		from	pls_intercambio_plano
		where	nr_seq_plano		= nr_seq_plano_p
		and	nr_seq_intercambio	= nr_seq_intercambio_p
		and	nr_sequencia		<> nr_seq_interc_plano_p;

		select	ie_tipo_contratacao,
			ie_tipo_operacao,
			ie_preco
		into STRICT	ie_tipo_contratacao_w,
			ie_tipo_operacao_w,
			ie_preco_w
		from	pls_plano
		where	nr_sequencia	= nr_seq_plano_p;

		if (cont_w > 0) then
			ds_erro_w	:= wheb_mensagem_pck.get_texto(279259);
		end if;

		if (ie_tipo_operacao_int_w <> ie_tipo_operacao_w) then
			ds_erro_w	:= wheb_mensagem_pck.get_texto(279260);
		end if;

		if (ie_tipo_estipulante_w = 'F') and (ie_tipo_contratacao_w <> 'I') then
			ds_erro_w	:= wheb_mensagem_pck.get_texto(279261);
		elsif (ie_tipo_estipulante_w = 'J') and (ie_tipo_contratacao_w = 'I') then
			ds_erro_w	:= wheb_mensagem_pck.get_texto(279262);
		end if;

		/*aaschlote 27/06/2013 - Tratamento quando é chamado no Java Swing, para não precisar realizar o comando sql antes de chamar a procedure*/

		if (ie_preco_w = '1') and (coalesce(nr_seq_tabela_p::text, '') = '') then
			ds_erro_w	:= wheb_mensagem_pck.get_texto(59470);
		end if;

		if (nr_seq_tabela_p IS NOT NULL AND nr_seq_tabela_p::text <> '') then
			select	nr_seq_plano,
				trunc(dt_inicio_vigencia)
			into STRICT	nr_seq_plano_w,
				dt_inicio_vigencia_w
			from	pls_tabela_preco
			where	nr_sequencia	= nr_seq_tabela_p;

			if (dt_inicio_vigencia_w > dt_inclusao_w) then
				ds_erro_w	:= wheb_mensagem_pck.get_texto(279265);
			end if;

			if (nr_seq_plano_w <> nr_seq_plano_p) then
				ds_erro_w	:= wheb_mensagem_pck.get_texto(279269);
			end if;
		end if;
	else
		ds_erro_w	:= wheb_mensagem_pck.get_texto(279272);
	end if;
else
	ds_erro_w	:= wheb_mensagem_pck.get_texto(279275);
end if;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_consiste_intercambio_plano ( nr_seq_intercambio_p bigint, nr_seq_plano_p bigint, nr_seq_tabela_p bigint, nr_seq_interc_plano_p bigint, ds_erro_p INOUT text) FROM PUBLIC;

