CREATE OR REPLACE PROCEDURE pls_consiste_obito_segurado ( nr_seq_segurado_p bigint, nr_seq_motivo_res_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_obito_w		varchar(1);
cd_pessoa_fisica_w	varchar(10);
qt_obitos_w		bigint;
			

BEGIN 
 
begin 
select	coalesce(ie_obito,'N') 
into STRICT	ie_obito_w 
from	pls_motivo_cancelamento 
where	nr_sequencia	= nr_seq_motivo_res_p;
exception 
when others then 
	ie_obito_w	:= 'N';
end;
 
select	max(cd_pessoa_fisica) 
into STRICT	cd_pessoa_fisica_w 
from	pls_segurado 
where	nr_sequencia	= nr_seq_segurado_p;
 
select	count(*) 
into STRICT	qt_obitos_w 
from	pessoa_fisica 
where	cd_pessoa_fisica = cd_pessoa_fisica_w 
and	(nr_certidao_obito IS NOT NULL AND nr_certidao_obito::text <> '') 
and	(dt_obito IS NOT NULL AND dt_obito::text <> '');
 
if (qt_obitos_w = 0) and (ie_obito_w = 'S')then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(183276,'NM_BENEFICIARIO='||substr(pls_obter_dados_segurado(nr_seq_segurado_p,'N'),1,255));
end if;				
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
