CREATE OR REPLACE PROCEDURE pls_consiste_reajuste_contrato ( dt_reajuste_p timestamp, dt_periodo_inicial_p timestamp, dt_periodo_final_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ie_reajuste_faixa_etaria_w	varchar(1);
qt_registro_w			integer	:= 0;
dt_inicio_w			timestamp;
dt_final_w			timestamp;
ds_erro_w			varchar(2000);


BEGIN
ds_erro_w	:= '';

/* Leitura do parâmetro 32 da OPS - Gestão de contratos */

begin
select	coalesce(max(obter_valor_param_usuario(1202, 32, Obter_Perfil_Ativo, nm_usuario_p, 0)), 'S')
into STRICT	ie_reajuste_faixa_etaria_w
;
exception
when others then
	ie_reajuste_faixa_etaria_w	:= 'S';
end;

dt_inicio_w	:= trunc(dt_reajuste_p, 'month');
dt_final_w	:= last_day(dt_reajuste_p);

select	count(*)
into STRICT	qt_registro_w
from 	pls_lote_reaj_segurado
where	trunc(dt_mesano_referencia) between trunc(dt_inicio_w) and trunc(dt_final_w);

if (ie_reajuste_faixa_etaria_w = 'S') and (qt_registro_w = 0) then
	ds_erro_w	:= ds_erro_w || wheb_mensagem_pck.get_texto(280873);
end if;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
