CREATE OR REPLACE PROCEDURE pls_consiste_regra_desconto ( nr_seq_preco_regra_p bigint, ie_tipo_regra_p text, nr_seq_contrato_p bigint, qt_min_vidas_p bigint, qt_max_vidas_p bigint) AS $body$
DECLARE


ds_regra_w		varchar(255);
qt_min_vidas_w		bigint;
qt_max_vidas_w		bigint;
ie_tipo_regra_w		varchar(1);
ie_erro_w		varchar(1);

c01 CURSOR FOR
	SELECT 	a.qt_min_vidas,
		a.qt_max_vidas,
		b.ie_tipo_regra
	from	pls_preco_regra a,
		pls_regra_desconto b
	where	a.nr_seq_regra = b.nr_sequencia
	and	b.nr_seq_contrato = nr_seq_contrato_p
	and	a.nr_sequencia <> nr_seq_preco_regra_p
	and	b.ie_situacao = 'A';


BEGIN

ie_erro_w	:= 'N';

open c01;
loop
fetch c01 into
	qt_min_vidas_w,
	qt_max_vidas_w,
	ie_tipo_regra_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if	((qt_min_vidas_w between qt_min_vidas_p and qt_max_vidas_p) or (qt_max_vidas_w between qt_min_vidas_p and qt_max_vidas_p)) then
		ie_erro_w	:= 'S';
	end if;

	if (ie_erro_w = 'S') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(265221,'');
		--Mensagem: Quantidade de vidas em conflito com outra j√° cadastrada para o contrato. Verifique!
	end if;
	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
