SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consiste_regra_grupo_serv (nr_seq_grupo_p bigint, nm_usuario_p text, ie_regra_procedimento_p text) AS $body$
DECLARE


qt_estrutura_w		bigint;
qt_vazio_w		bigint;
qt_nao_liberado_w	bigint;
ie_regra_procedimento_w varchar(1);


BEGIN

ie_regra_procedimento_w := ie_regra_procedimento_p;

select	count(1)
into STRICT	qt_estrutura_w
from	pls_preco_servico a
where	a.nr_seq_grupo	= nr_seq_grupo_p
and	((a.cd_area_procedimento IS NOT NULL AND a.cd_area_procedimento::text <> '') or (a.cd_especialidade IS NOT NULL AND a.cd_especialidade::text <> '') or (a.cd_grupo_proc IS NOT NULL AND a.cd_grupo_proc::text <> ''))  LIMIT 1;

select	count(1)
into STRICT	qt_vazio_w
from	pls_preco_servico a
where	a.nr_seq_grupo	= nr_seq_grupo_p
and	coalesce(a.cd_procedimento::text, '') = ''  LIMIT 1;

select	count(1)
into STRICT	qt_nao_liberado_w
from	pls_preco_servico a
where	a.nr_seq_grupo	= nr_seq_grupo_p
and (coalesce(a.ie_estrutura::text, '') = '' or a.ie_estrutura = 'N')  LIMIT 1;

if (coalesce(ie_regra_procedimento_w,'N') = 'S') then
	if (qt_estrutura_w > 0 or qt_vazio_w > 0 or qt_nao_liberado_w > 0) then
		--"Há regras mais abragentes que procedimento ou que não permitem liberação, verifique"
		CALL wheb_mensagem_pck.exibir_mensagem_abort(213243);
	end if;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
