CREATE OR REPLACE PROCEDURE pls_consiste_resp_financ_vigen ( nr_sequencia_p bigint, nr_seq_congenere_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w		bigint;


BEGIN
select	count(*)
into STRICT	qt_registro_w
from	pls_congenere_resp_financ
where	coalesce(dt_fim_vigencia::text, '') = ''
and 	nr_seq_congenere	= nr_seq_congenere_p
and	nr_sequencia	       <> nr_sequencia_p;

if (qt_registro_w <> 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 267211, null ); /* Não é possivel salvar mais de um responsável financeiro vigente para a mesma cooperativa. */
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
