SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_acomod_diaria ( nr_seq_guia_proc_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Verificar se existe regra de diárias X acomodações e gerar glosa caso a regra exista e o procedimento da guia não constar na regra.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  X ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_guia_w			bigint;
nr_seq_tipo_acomodacao_w	bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
nr_seq_reg_glosa_w		bigint;
qt_registros_w			bigint;
ie_gerar_glosa_w		varchar(1);
nr_seq_prestador_w		bigint;
ie_classificacao_w		varchar(1);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_regra_glosa_acom
	where	nr_seq_tipo_acomodacao 	= nr_seq_tipo_acomodacao_w
	and 	ie_situacao 		= 'A'
	and (coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento and pls_obter_se_controle_estab('RE') = 'S')
	
UNION

	SELECT	nr_sequencia
	from	pls_regra_glosa_acom
	where	nr_seq_tipo_acomodacao 	= nr_seq_tipo_acomodacao_w
	and 	ie_situacao 		= 'A';


BEGIN
begin
	select 	nr_seq_guia
	into STRICT	nr_seq_guia_w
	from	pls_guia_plano_proc
	where	nr_sequencia = nr_seq_guia_proc_p;
exception
when others then
	nr_seq_guia_w := null;
end;

begin
	select 	nr_seq_tipo_acomodacao,
		nr_seq_prestador
	into STRICT	nr_seq_tipo_acomodacao_w,
		nr_seq_prestador_w
	from	pls_guia_plano
	where	nr_sequencia	= nr_seq_guia_w;
exception
when others then
	nr_seq_tipo_acomodacao_w 	:= null;
	nr_seq_prestador_w 		:= null;
end;

if (nr_seq_tipo_acomodacao_w IS NOT NULL AND nr_seq_tipo_acomodacao_w::text <> '')	then
	begin
		select 	cd_procedimento,
			ie_origem_proced
		into STRICT	cd_procedimento_w,
			ie_origem_proced_w
		from	pls_guia_plano_proc
		where 	nr_sequencia	= nr_seq_guia_proc_p;
	exception
	when others then
		cd_procedimento_w 	:= null;
		ie_origem_proced_w 	:= null;
	end;

	begin
		select 	ie_classificacao
		into STRICT	ie_classificacao_w
		from	procedimento
		where	cd_procedimento		= cd_procedimento_w
		and 	ie_origem_proced	= ie_origem_proced_w;
	exception
	when others then
		ie_classificacao_w	:= null;
	end;

	if (ie_classificacao_w = '3') then
		open C01;
		loop
		fetch C01 into
			nr_seq_reg_glosa_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			ie_gerar_glosa_w := 'S';

			select 	count(1)
			into STRICT	qt_registros_w
			from	pls_glosa_proc_acom
			where	nr_seq_regra_acom	= nr_seq_reg_glosa_w
			and	cd_procedimento		= cd_procedimento_w
			and 	ie_origem_proced	= ie_origem_proced_w;

			if (qt_registros_w <> 0) then
				ie_gerar_glosa_w := 'N';
				exit;
			end if;
			end;
		end loop;
		close C01;
		--Alterado de 1815 para 1402 OS 757971
		if (ie_gerar_glosa_w = 'S') then
			CALL pls_gravar_motivo_glosa(	'1402',null,nr_seq_guia_proc_p,
							null,'Procedimento não autorizado',nm_usuario_p,
							'','CG',nr_seq_prestador_w,
							null,null);
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_consistir_acomod_diaria ( nr_seq_guia_proc_p bigint, nm_usuario_p text) FROM PUBLIC;

