CREATE OR REPLACE PROCEDURE pls_consistir_agravo ( nr_seq_contrato_p bigint, nr_seq_segurado_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

 
nr_seq_segurado_w	bigint;
nr_seq_agravo_w		bigint;
qt_parcelas_cobradas_w	bigint;
ds_erro_w		varchar(255);

C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_segurado 
	where	nr_seq_contrato	= nr_seq_contrato_p 
	and	coalesce(dt_rescisao::text, '') = '';
			
C02 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_segurado_agravo 
	where	nr_seq_segurado	= nr_seq_segurado_w;

C03 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_segurado_agravo 
	where	nr_seq_segurado	= nr_seq_segurado_p;	

BEGIN 
 
if (coalesce(nr_seq_segurado_p,0) = 0) then 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_segurado_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		open C02;
		loop 
		fetch C02 into	 
			nr_seq_agravo_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin 
			 
			select	count(*) 
			into STRICT	qt_parcelas_cobradas_w 
			from	pls_segurado_agravo_parc 
			where	nr_seq_segurado_agravo	= nr_seq_agravo_w 
			and	coalesce(nr_seq_mensalidade_item::text, '') = '' 
			and	cd_estabelecimento	= cd_estabelecimento_p;
			 
			if (qt_parcelas_cobradas_w	> 0) then 
				ds_erro_w := wheb_mensagem_pck.get_texto(280702, 'NR_SEQ_SEGURADO_P=' || substr(pls_obter_dados_segurado(nr_seq_segurado_w,'N'),1,255));
			end if;							
			 
			end;
		end loop;
		close C02;
		 
		end;
	end loop;
	close C01;
elsif (coalesce(nr_seq_segurado_p,0) <> 0) then 
	open C03;
	loop 
	fetch C03 into	 
		nr_seq_agravo_w;
	EXIT WHEN NOT FOUND; /* apply on C03 */
		begin 
		select	count(*) 
			into STRICT	qt_parcelas_cobradas_w 
			from	pls_segurado_agravo_parc 
			where	nr_seq_segurado_agravo	= nr_seq_agravo_w 
			and	coalesce(nr_seq_mensalidade_item::text, '') = '' 
			and	cd_estabelecimento	= cd_estabelecimento_p;
			 
			if (qt_parcelas_cobradas_w	> 0) then 
				ds_erro_w := wheb_mensagem_pck.get_texto(280702, 'NR_SEQ_SEGURADO_P=' || substr(pls_obter_dados_segurado(nr_seq_segurado_p,'N'),1,255));
			end if;		
		end;
	end loop;
	close C03;
end if;	
 
ds_erro_p	:= ds_erro_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
