CREATE OR REPLACE PROCEDURE pls_consistir_alt_inclus_benef ( nr_seq_inclusao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

			
qt_registros_w		bigint;
ie_consistir_sib_w	varchar(10);
ds_erro_w		varchar(400);


BEGIN

ie_consistir_sib_w	:= coalesce(obter_valor_param_usuario(1232, 65, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p), 'N');

if (ie_consistir_sib_w	= 'S') then
	select	count(*)
	into STRICT	qt_registros_w
	from	pls_inconsist_incl_benef
	where	nr_seq_solic_inclusao	= nr_seq_inclusao_p
	and	coalesce(dt_liberacao::text, '') = '';
	
	if (qt_registros_w	> 0) then
		ds_erro_w	:= wheb_mensagem_pck.get_texto(280850);
	end if;
end if;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
