SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_anexo_guia_aut ( nr_seq_lote_guia_aut_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_guia_referencia_w	varchar(20);
nr_seq_lote_guia_aut_w	bigint;
qt_registros_w		bigint;
qt_registros_glosa_w	bigint;
ie_status_w		varchar(1);
nr_seq_guia_w		bigint;
						

BEGIN 
 
begin 
select	nr_seq_guia, 
	cd_guia_referencia 
into STRICT	nr_seq_guia_w, 
	cd_guia_referencia_w 
from	pls_lote_anexo_guias_aut 
where	nr_sequencia = nr_seq_lote_guia_aut_p;
exception 
when others then 
	nr_seq_guia_w		:= null;
	cd_guia_referencia_w	:= null;
end;
 
qt_registros_glosa_w	:= 0;
 
if (coalesce(cd_guia_referencia_w::text, '') = '') then 
	/* cmgross - 05/06/13 - Consiste a glosa '1303' */
 
	CALL pls_inserir_glosa_anexo_guia(1303, nr_seq_lote_guia_aut_p, null, null, nm_usuario_p);
	qt_registros_glosa_w	:= 1;
else	 
	select	count(1) 
	into STRICT	qt_registros_w 
	from	pls_guia_plano 
	where	cd_guia	= cd_guia_referencia_w;
	 
	if (qt_registros_w = 0) then 
		/* cmgross - 05/06/13 - Consiste a glosa '1306' */
 
		CALL pls_inserir_glosa_anexo_guia(1306, nr_seq_lote_guia_aut_p, null, null, nm_usuario_p);
		qt_registros_glosa_w	:= 1;
	else 
		select	count(1) 
		into STRICT	qt_registros_w 
		from 	pls_conta 
		where	cd_guia = cd_guia_referencia_w;
		 
		if (qt_registros_w > 0) then 
			/* Guia jÃ¡ apresentada */
 
			CALL pls_inserir_glosa_anexo_guia(1308, nr_seq_lote_guia_aut_p, null, null, nm_usuario_p);
			qt_registros_glosa_w	:= 1;
		end if;
	end if;
end if;	
 
if (qt_registros_glosa_w = 0) then 
	/* cmgross - 05/06/13 - Consiste as glosas '1214', '1802', '1803', '1806', '1815', '9920' */
 
	CALL pls_agt_consistir_guia_proc(nr_seq_lote_guia_aut_p, nm_usuario_p);
	 
	/* cmgross - 05/06/13 - Consiste a glosa '2006' */
 
	CALL pls_agt_consistir_guia_mat(nr_seq_lote_guia_aut_p, nr_seq_guia_w, nm_usuario_p);
end if;
 
select	count(1) 
into STRICT	qt_registros_glosa_w 
from	pls_lote_anexo_glosa_aut 
where	nr_seq_lote_guia_aut = nr_seq_lote_guia_aut_p;
 
ie_status_w := '1';
if (qt_registros_glosa_w > 0) then 
	ie_status_w := '3';
end if;
 
update	pls_lote_anexo_guias_aut 
set	ie_status = ie_status_w 
where	nr_sequencia = nr_seq_lote_guia_aut_p;	
 
update	pls_lote_anexo_proc_aut 
set	ie_status = ie_status_w 
where	nr_seq_lote_anexo_guia = nr_seq_lote_guia_aut_p;	
 
update	pls_lote_anexo_mat_aut 
set	ie_status = ie_status_w 
where	nr_seq_lote_anexo_guia = nr_seq_lote_guia_aut_p;	
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
