SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_area_prestador ( nr_seq_guia_p bigint, nr_seq_conta_p bigint, nr_seq_requisicao_p bigint, ie_consistencia_p INOUT text) AS $body$
DECLARE


ie_tipo_consistencia_w		varchar(1);
nr_seq_segurado_w		bigint;
nr_seq_contrato_w		bigint;
nr_seq_prestador_w		bigint;
cd_municipio_ibge_prest_w	sus_municipio.cd_municipio_ibge%type;
sg_estado_prest_w		pls_prestador_area.sg_estado%type;
nr_seq_regiao_prest_w		bigint;
cd_municipio_ibge_seg_w		sus_municipio.cd_municipio_ibge%type;
sg_estado_seg_w			pls_extensao_area.sg_estado%type;
nr_seq_regiao_seg_w		bigint;
cd_municipio_ibge_contr_w	sus_municipio.cd_municipio_ibge%type;
sg_estado_contr_w		pls_extensao_area.sg_estado%type;
nr_seq_regiao_contr_w		bigint;
ds_unidade_federacao_contr_w	varchar(2);
ds_unidade_federacao_seg_w	varchar(2);
ds_unidade_federacao_prest_w	varchar(2);
qt_extensao_area_seg_w		bigint;
qt_extensao_area_contr_w	bigint;
nr_seq_uni_exec_w		bigint;
ie_tipo_processo_w		varchar(1)	:= 'O';
nr_seq_intercambio_w		pls_intercambio.nr_sequencia%type;
dt_sysdate_w			timestamp;
nr_seq_prestador_regra_w	pls_extensao_area.nr_seq_prestador%type;
nr_seq_plano_w			pls_extensao_area.nr_seq_plano%type;

c01 CURSOR FOR
	SELECT	cd_municipio_ibge,
		sg_estado,
		nr_seq_regiao
	from	pls_prestador_area
	where	nr_seq_prestador	= nr_seq_prestador_w
	and	ie_tipo_processo_w	= 'O'
	
union	all

	PERFORM	cd_municipio_ibge,
		sg_estado,
		nr_seq_regiao
	from	pls_cooperativa_area
	where	nr_seq_cooperativa	= nr_seq_uni_exec_w
	and	ie_tipo_processo_w	= 'I';


c02 CURSOR FOR
	SELECT	cd_municipio_ibge,
		sg_estado,
		nr_seq_regiao,
		nr_seq_prestador
	from	pls_extensao_area
	where	nr_seq_segurado		= nr_seq_segurado_w
	and	dt_sysdate_w between trunc(dt_inicio_vigencia) and coalesce(trunc(dt_fim_vigencia),dt_sysdate_w);

c03 CURSOR FOR
	SELECT	cd_municipio_ibge,
		sg_estado,
		nr_seq_regiao,
		nr_seq_prestador
	from	pls_extensao_area
	where	nr_seq_contrato		= nr_seq_contrato_w
	and	dt_sysdate_w between trunc(dt_inicio_vigencia) and coalesce(trunc(dt_fim_vigencia),dt_sysdate_w)
	
union	all
	
	PERFORM	cd_municipio_ibge,
		sg_estado,
		nr_seq_regiao,
		nr_seq_prestador
	from	pls_extensao_area
	where	nr_seq_intercambio	= nr_seq_intercambio_w
	and	dt_sysdate_w between trunc(dt_inicio_vigencia) and coalesce(trunc(dt_fim_vigencia),dt_sysdate_w)
	
union	all
	
	select	cd_municipio_ibge,
		sg_estado,
		nr_seq_regiao,
		nr_seq_prestador
	from	pls_extensao_area
	where	nr_seq_plano		= nr_seq_plano_w
	and	dt_sysdate_w between trunc(dt_inicio_vigencia) and coalesce(trunc(dt_fim_vigencia),dt_sysdate_w);	

BEGIN

dt_sysdate_w := trunc(clock_timestamp());

if (coalesce(nr_seq_guia_p,0) > 0) then
	ie_tipo_consistencia_w	:= 'G';
elsif (coalesce(nr_seq_conta_p,0) > 0) then
	ie_tipo_consistencia_w	:= 'C';
elsif (coalesce(nr_seq_requisicao_p,0) > 0) then
	ie_tipo_consistencia_w	:= 'R';
end if;

if (ie_tipo_consistencia_w = 'G') then
	select	nr_seq_segurado,
		nr_seq_prestador,
		nr_seq_uni_exec
	into STRICT	nr_seq_segurado_w,
		nr_seq_prestador_w,
		nr_seq_uni_exec_w
	from	pls_guia_plano
	where	nr_sequencia	= nr_seq_guia_p;
	
	if (coalesce(nr_seq_prestador_w,0) = 0) and (coalesce(nr_seq_uni_exec_w,0) > 0) then
		ie_tipo_processo_w	:= 'I';
	end if;
elsif (ie_tipo_consistencia_w = 'C') then
	select	nr_seq_segurado,
		nr_seq_prestador_exec
	into STRICT	nr_seq_segurado_w,
		nr_seq_prestador_w
	from	pls_conta
	where	nr_sequencia	= nr_seq_conta_p;
elsif (ie_tipo_consistencia_w = 'R') then
	select	nr_seq_segurado,
		nr_seq_prestador,
		nr_seq_uni_exec
	into STRICT	nr_seq_segurado_w,
		nr_seq_prestador_w,
		nr_seq_uni_exec_w
	from	pls_requisicao
	where	nr_sequencia	= nr_seq_requisicao_p;
	
	if (coalesce(nr_seq_prestador_w,0) = 0) and (coalesce(nr_seq_uni_exec_w,0) > 0) then
		ie_tipo_processo_w	:= 'I';
	end if;
end if;

if (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then
	nr_seq_contrato_w	:=	pls_obter_dados_segurado(nr_seq_segurado_w,'NC');
	nr_seq_intercambio_w	:=	pls_obter_dados_segurado(nr_seq_segurado_w, 'NRCI');
	nr_seq_plano_w		:=	(pls_obter_dados_segurado(nr_seq_segurado_w, 'NRP'))::numeric;
end if;

select	count(1)
into STRICT	qt_extensao_area_seg_w
from	pls_extensao_area
where	nr_seq_segurado		= nr_seq_segurado_w
and	dt_sysdate_w between dt_inicio_vigencia and coalesce(dt_fim_vigencia,dt_sysdate_w);

select	count(1)
into STRICT	qt_extensao_area_contr_w
from	pls_extensao_area
where	nr_seq_contrato		= nr_seq_contrato_w
and	dt_sysdate_w between dt_inicio_vigencia and coalesce(dt_fim_vigencia,dt_sysdate_w);

if (qt_extensao_area_contr_w = 0) then
	select	count(1)
	into STRICT	qt_extensao_area_contr_w
	from	pls_extensao_area
	where	nr_seq_intercambio	= nr_seq_intercambio_w
	and	dt_sysdate_w between dt_inicio_vigencia and coalesce(dt_fim_vigencia,dt_sysdate_w);
end if;

if (qt_extensao_area_seg_w = 0) and (qt_extensao_area_contr_w = 0) then
	ie_consistencia_p	:= 'S';
else	
	ie_consistencia_p	:= 'N';
end if;

open C01;
loop
fetch C01 into	
	cd_municipio_ibge_prest_w,
	sg_estado_prest_w,
	nr_seq_regiao_prest_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	if (coalesce(cd_municipio_ibge_prest_w,'0') <> '0') then
		select	max(ds_unidade_federacao)
		into STRICT	ds_unidade_federacao_prest_w
		from	sus_municipio
		where	cd_municipio_ibge	= cd_municipio_ibge_prest_w;
	end if;		
		/* Verificar are de atuacao do segurado*/
		open C02;
		loop
		fetch C02 into	
			cd_municipio_ibge_seg_w,
			sg_estado_seg_w,
			nr_seq_regiao_seg_w,
			nr_seq_prestador_regra_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin
			if (coalesce(cd_municipio_ibge_seg_w,'0') <> '0') then
				select	max(ds_unidade_federacao)
				into STRICT	ds_unidade_federacao_seg_w
				from	sus_municipio
				where	cd_municipio_ibge	= cd_municipio_ibge_seg_w;
			end if;
			
			if (cd_municipio_ibge_seg_w IS NOT NULL AND cd_municipio_ibge_seg_w::text <> '') and (cd_municipio_ibge_prest_w IS NOT NULL AND cd_municipio_ibge_prest_w::text <> '') and (cd_municipio_ibge_seg_w = cd_municipio_ibge_prest_w) then
				ie_consistencia_p	:= 'S';
				exit;	
			elsif (sg_estado_seg_w IS NOT NULL AND sg_estado_seg_w::text <> '') and (sg_estado_prest_w IS NOT NULL AND sg_estado_prest_w::text <> '') and (sg_estado_seg_w = sg_estado_prest_w) then
				ie_consistencia_p	:= 'S';
				exit;
			elsif (nr_seq_regiao_seg_w = nr_seq_regiao_prest_w) then
				ie_consistencia_p	:= 'S';
				exit;	
			elsif (pls_obter_se_mun_uf_regiao(cd_municipio_ibge_seg_w,sg_estado_seg_w,nr_seq_regiao_prest_w) = 'S') then
				ie_consistencia_p	:= 'S';
				exit;
			elsif (pls_obter_se_mun_uf_regiao(cd_municipio_ibge_prest_w,sg_estado_prest_w,nr_seq_regiao_seg_w) = 'S') then
				ie_consistencia_p	:= 'S';
				exit;
			elsif (ds_unidade_federacao_seg_w	= sg_estado_prest_w) then
				ie_consistencia_p	:= 'S';
				exit;
			elsif (ds_unidade_federacao_prest_w	= sg_estado_seg_w) then
				ie_consistencia_p	:= 'S';
				exit;
			elsif (nr_seq_prestador_regra_w	= nr_seq_prestador_w) and (ie_tipo_processo_w <> 'I') then
				ie_consistencia_p	:= 'S';
				exit;
			end if;	
			end;
		end loop;
		close C02;
		
		/* Verificar area de atuacao do contrato */
		open C03;
		loop
		fetch C03 into	
			cd_municipio_ibge_contr_w,
			sg_estado_contr_w,
			nr_seq_regiao_contr_w,
			nr_seq_prestador_regra_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
			begin

			if (coalesce(cd_municipio_ibge_contr_w,'0') <> '0') then
				select	max(ds_unidade_federacao)
				into STRICT	ds_unidade_federacao_contr_w
				from	sus_municipio
				where	cd_municipio_ibge	= cd_municipio_ibge_contr_w;
			end if;
			
			if (cd_municipio_ibge_contr_w IS NOT NULL AND cd_municipio_ibge_contr_w::text <> '') and (cd_municipio_ibge_prest_w IS NOT NULL AND cd_municipio_ibge_prest_w::text <> '') and (cd_municipio_ibge_contr_w = cd_municipio_ibge_prest_w) then
				ie_consistencia_p	:= 'S';
				exit;
			elsif (sg_estado_prest_w IS NOT NULL AND sg_estado_prest_w::text <> '') and (sg_estado_contr_w IS NOT NULL AND sg_estado_contr_w::text <> '') and (sg_estado_contr_w = sg_estado_prest_w) then
				ie_consistencia_p	:= 'S';
				exit;
			elsif (nr_seq_regiao_contr_w = nr_seq_regiao_prest_w) then
				ie_consistencia_p	:= 'S';
				exit;
			elsif (pls_obter_se_mun_uf_regiao(cd_municipio_ibge_contr_w,sg_estado_contr_w,nr_seq_regiao_prest_w) = 'S') then
				ie_consistencia_p	:= 'S';
				exit;
			elsif (pls_obter_se_mun_uf_regiao(cd_municipio_ibge_prest_w,sg_estado_prest_w,nr_seq_regiao_contr_w) = 'S') then
				ie_consistencia_p	:= 'S';
				exit;
			elsif (ds_unidade_federacao_contr_w	= sg_estado_prest_w) then
				ie_consistencia_p	:= 'S';
				exit;
			elsif (ds_unidade_federacao_prest_w	= sg_estado_contr_w) then
				ie_consistencia_p	:= 'S';
				exit;
			elsif (nr_seq_prestador_regra_w	= nr_seq_prestador_w) and (ie_tipo_processo_w <> 'I') then
				ie_consistencia_p	:= 'S';
				exit;
			end if;	
			end;
		end loop;
		close C03;
	end;
	
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_consistir_area_prestador ( nr_seq_guia_p bigint, nr_seq_conta_p bigint, nr_seq_requisicao_p bigint, ie_consistencia_p INOUT text) FROM PUBLIC;

