CREATE OR REPLACE PROCEDURE pls_consistir_baixa_desc_folha ( nr_seq_cobranca_p bigint, ie_checados_p text, nm_usuario_p text, ds_mensagem_p INOUT text) AS $body$
DECLARE


qt_titulos_w	bigint;
vl_credito_w	double precision;
vl_cobranca_w	double precision;


BEGIN

select	count(*)
into STRICT	qt_titulos_w
from	titulo_receber_cobr
where	nr_seq_cobranca	= nr_seq_cobranca_p
and	((ie_checados_p = 'N') or ((ie_checados_p = 'S') and ie_verificacao = 'S'));

select	sum(vl_liquidacao),
	sum(vl_cobranca)
into STRICT	vl_credito_w,
	vl_cobranca_w
from	titulo_receber_cobr
where	nr_seq_cobranca	= nr_seq_cobranca_p
and	((ie_checados_p = 'N') or ((ie_checados_p = 'S') and ie_verificacao = 'S'));

ds_mensagem_p	:='Deseja realizar a baixa de R$' || vl_credito_w || ', referente à ' || qt_titulos_w || ' títulos no valor de R$' || vl_cobranca_w || '?';

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
