SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_benef_proc ( nr_seq_segurado_p bigint, nr_seq_guia_p bigint, nr_seq_conta_proc_p pls_conta_proc_v.nr_sequencia%type, nr_seq_req_proc_p bigint, ie_evento_p text, nm_usuario_p text, cd_estabelecimento_p bigint, qt_idade_p bigint) AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(10);
qt_idade_w			integer;
nr_seq_guia_proc_w		bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
qt_idade_permitida_w		smallint;
qt_sexo_permitido_w		smallint;
nr_seq_prestador_w		bigint;
ie_tipo_despesa_w		varchar(1);
ie_tipo_segurado_w		varchar(5);
ie_guia_autorizada_w		varchar(1);
ie_autorizado_w			varchar(1)	:= 'N';
ie_sexo_w			pessoa_fisica.ie_sexo%type;
ie_sexo_permitido_w		procedimento.ie_sexo_sus%type;
nr_seq_conta_w			pls_conta_v.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_procedimento,
		ie_origem_proced
	from	pls_guia_plano_proc
	where	nr_seq_guia = nr_seq_guia_p;


BEGIN

begin
select	b.cd_pessoa_fisica,
	b.ie_sexo,
	a.ie_tipo_segurado
into STRICT	cd_pessoa_fisica_w,
	ie_sexo_w,
	ie_tipo_segurado_w
from	pls_segurado a,
	pessoa_fisica b
where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
and	a.nr_sequencia 		= nr_seq_segurado_p;
exception
when others then
	cd_pessoa_fisica_w	:= '';
	ie_sexo_w		:= '';
end;

qt_idade_w	:= qt_idade_p;

if (coalesce(qt_idade_w::text, '') = '') then
	qt_idade_w := pls_obter_idade_segurado(nr_seq_segurado_p, clock_timestamp(), 'A');
end if;

if (coalesce(nr_seq_guia_p,0) <> 0) then
	select	nr_seq_prestador
	into STRICT	nr_seq_prestador_w
	from	pls_guia_plano
	where	nr_sequencia = nr_seq_guia_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_guia_proc_w,
		cd_procedimento_w,
		ie_origem_proced_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

		select	count(1)
		into STRICT	qt_sexo_permitido_w
		from	procedimento
		where	cd_procedimento	= cd_procedimento_w
		and	ie_origem_proced	= ie_origem_proced_w
		and	((coalesce(ie_sexo_sus,ie_sexo_w) = ie_sexo_w) or (ie_sexo_sus = 'I'));

		if (qt_sexo_permitido_w = 0) and (ie_tipo_segurado_w not in ('I','H')) then /*Diego OS - 299659 - Não será gerada esta glosa para usuário eventual*/
			CALL pls_gravar_motivo_glosa('1802',null,nr_seq_guia_proc_w,null,'',nm_usuario_p,'', ie_evento_p,nr_seq_prestador_w, null,null);
		end if;

		select	count(1)
		into STRICT	qt_idade_permitida_w
		from	procedimento
		where	cd_procedimento		= cd_procedimento_w
		and	ie_origem_proced	= ie_origem_proced_w
		and	qt_idade_w between coalesce(qt_idade_minima_sus,qt_idade_w) and coalesce(qt_idade_maxima_sus,qt_idade_w);

		if (qt_idade_permitida_w = 0) and (ie_tipo_segurado_w not in ('I','H')) then /*Diego OS - 299659 - Não será gerada esta glosa para usuário eventual*/
			CALL pls_gravar_motivo_glosa('1803',null,nr_seq_guia_proc_w,null,'',nm_usuario_p,'', ie_evento_p,nr_seq_prestador_w, null,null);
		end if;

	end loop;
	close C01;

elsif (coalesce(nr_seq_req_proc_p,0)	<> 0) then
	select	a.nr_seq_prestador,
		b.cd_procedimento,
		b.ie_origem_proced
	into STRICT	nr_seq_prestador_w,
		cd_procedimento_w,
		ie_origem_proced_w
	from	pls_requisicao_proc	b,
		pls_requisicao		a
	where	a.nr_sequencia		= b.nr_seq_requisicao
	and	b.nr_sequencia		= nr_seq_req_proc_p;

	select	count(1)
	into STRICT	qt_sexo_permitido_w
	from	procedimento
	where	cd_procedimento			= cd_procedimento_w
	and	ie_origem_proced		= ie_origem_proced_w
	and	((coalesce(ie_sexo_sus,ie_sexo_w) 	= ie_sexo_w) or (ie_sexo_sus = 'I'));

	if (qt_sexo_permitido_w = 0) and (ie_tipo_segurado_w not in ('I','H')) then /*Diego OS - 299659 - Não será gerada esta glosa para usuário eventual*/
		CALL pls_gravar_requisicao_glosa(	'1802', null, nr_seq_req_proc_p,
						null, '', nm_usuario_p,
						nr_seq_prestador_w, cd_estabelecimento_p, null,
						'');
	end if;

	select	count(1)
	into STRICT	qt_idade_permitida_w
	from	procedimento
	where	cd_procedimento		= cd_procedimento_w
	and	ie_origem_proced	= ie_origem_proced_w
	and	qt_idade_w between coalesce(qt_idade_minima_sus,qt_idade_w) and coalesce(qt_idade_maxima_sus,qt_idade_w);

	if (qt_idade_permitida_w = 0) and (ie_tipo_segurado_w not in ('I','H')) then /*Diego OS - 299659 - Não será gerada esta glosa para usuário eventual*/
		CALL pls_gravar_requisicao_glosa(	'1803', null, nr_seq_req_proc_p,
						null, '', nm_usuario_p,
						nr_seq_prestador_w, cd_estabelecimento_p, null,
						'');
	end if;

-- Verificar o procedimento
elsif (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then

	select	a.nr_seq_conta,
		a.cd_procedimento,
		a.ie_origem_proced,
		b.nr_seq_prestador,
		a.ie_tipo_despesa
	into STRICT	nr_seq_conta_w,
		cd_procedimento_w,
		ie_origem_proced_w,
		nr_seq_prestador_w,
		ie_tipo_despesa_w
	from	pls_conta_proc	a,
		pls_conta	b
	where	a.nr_sequencia 	= nr_seq_conta_proc_p
	and	b.nr_sequencia	= a.nr_seq_conta;

	-- OS 545984 Dlehmkuhl 31/01/2013 - se tem guia autorizada não barra pela idade ou sexo
	ie_guia_autorizada_w	:= pls_obter_se_guia_aut(	nr_seq_conta_proc_p, cd_procedimento_w, ie_origem_proced_w,
								'P', cd_estabelecimento_p);

	if (coalesce(cd_procedimento_w,0) <> 0) and (ie_guia_autorizada_w	= 'N')	then

		-- Não será verificada a glosa 1802 para usuário eventual.
		if (ie_tipo_segurado_w not in ('I','H')) then

			-- Só verificar a glosa 1802 se o beneficiário tiver sexo informado e ele for diferente de indeterminado. Para sexo indeterminado não deve verificar a glosa
			-- pois é o mesmo que não tivesse sexo  informado. Se o beneficiário não tiver sexo informado deve ser gerada outra glosa, não 1802.
			if (ie_sexo_w IS NOT NULL AND ie_sexo_w::text <> '') and (ie_sexo_w <> 'I') then

				-- Obter o sexo exclusivo do procedimento.
				select	max(ie_sexo_sus)
				into STRICT	ie_sexo_permitido_w
				from	procedimento
				where	cd_procedimento		= cd_procedimento_w
				and	ie_origem_proced	= ie_origem_proced_w;

				-- Só irá verificar o sexo se o procedimento tiver sexo exclusivo informado e ele for diferente de indeterminado então compara com o sexo do beneficiário, pois
				-- o valor indeterminado significa o mesmo que não existe.
				if (ie_sexo_permitido_w IS NOT NULL AND ie_sexo_permitido_w::text <> '') and (ie_sexo_permitido_w <> 'I')  then

					-- verifica se o sexo do beneficiário é diferente do sexo exclusivo do procedimento, se for então gera a glosa 1802.
					if (ie_sexo_permitido_w <> ie_sexo_w) then

						-- Deve ser gravado sempre a conta com o procedimento junto.
						CALL pls_gravar_conta_glosa('1802', nr_seq_conta_w, nr_seq_conta_proc_p,
									null,'N','Verifique o campo Sexo exclusivo do procedimento na função Procedimentos.',
									nm_usuario_p,'A', ie_evento_p,
									nr_seq_prestador_w, cd_estabelecimento_p, '',null);
					end if;
				end if;
			end if;
		end if;

		/*Se for digitação de contas pelo portal ou complemento de contas verificar se a guia já está autorizada  Demitrius OS - 564987*/

		if (ie_evento_p = 'DC')	then
			ie_autorizado_w	:= pls_obter_se_guia_aut(nr_seq_conta_proc_p,cd_procedimento_w,ie_origem_proced_w,'P',cd_estabelecimento_p);
		end if;

		if (ie_autorizado_w	= 'N')	then

			select	count(1)
			into STRICT	qt_idade_permitida_w
			from	procedimento
			where	cd_procedimento		= cd_procedimento_w
			and	ie_origem_proced	= ie_origem_proced_w
			and	qt_idade_w between coalesce(qt_idade_minima_sus,qt_idade_w) and coalesce(qt_idade_maxima_sus,qt_idade_w);

			if (qt_idade_permitida_w = 0) and (ie_tipo_segurado_w not in ('I','H')) then /*Diego OS - 299659 - Não será gerada esta glosa para usuário eventual*/
				if (ie_tipo_despesa_w = '4') then

					-- Gravar sempre a conta com o procedimento junto.
					CALL pls_gravar_conta_glosa('2818',nr_seq_conta_w, nr_seq_conta_proc_p,
							null,'N','Verifique os campos Idade mín/máx do procedimento na função Procedimentos.',
							nm_usuario_p,'A', ie_evento_p,
							nr_seq_prestador_w, cd_estabelecimento_p, '', null);
				else
					-- Gravar sempre a conta com o procedimento junto.
					CALL pls_gravar_conta_glosa('1803', nr_seq_conta_w, nr_seq_conta_proc_p,
							null,'N','Verifique os campos Idade mín/máx do procedimento na função Procedimentos.',
							nm_usuario_p,'A', ie_evento_p,
							nr_seq_prestador_w, cd_estabelecimento_p, '', null);
				end if;
			end if;
		end if;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_consistir_benef_proc ( nr_seq_segurado_p bigint, nr_seq_guia_p bigint, nr_seq_conta_proc_p pls_conta_proc_v.nr_sequencia%type, nr_seq_req_proc_p bigint, ie_evento_p text, nm_usuario_p text, cd_estabelecimento_p bigint, qt_idade_p bigint) FROM PUBLIC;

