SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_benef_repasse ( nr_seq_segurado_p bigint, ds_cooperativa_p INOUT text) AS $body$
BEGIN

begin
select	substr(pls_obter_nome_congenere(coalesce(max(nr_seq_congenere_atend),max(nr_seq_congenere))),1,255)
into STRICT	ds_cooperativa_p
from	pls_segurado_repasse
where	nr_seq_segurado	= nr_seq_segurado_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	((coalesce(dt_fim_repasse::text, '') = '') or (dt_fim_repasse > clock_timestamp()));
exception
when others then
	ds_cooperativa_p	:= '';
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
