CREATE OR REPLACE PROCEDURE pls_consistir_cad_medico_coop (cd_pessoa_fisica_p text, cd_cgc_p text, dt_inclusao_p timestamp, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_cooperado_w	bigint;


BEGIN
select	max(nr_sequencia)
into STRICT	nr_seq_cooperado_w
from	pls_cooperado
where	((cd_pessoa_fisica 	= cd_pessoa_fisica_p) or (cd_cgc		= cd_cgc_p))
and	coalesce(dt_exclusao::text, '') = ''
and	nr_sequencia <> nr_sequencia_p;

if (nr_seq_cooperado_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265472,'SEQ_COOPERADO=' ||nr_seq_cooperado_w);
	--Este cooperado ja está cadastrado no Tasy! Seq ||nr_seq_cooperado_w
else
	nr_seq_cooperado_w := 0;
end if;

select	max(nr_sequencia)
into STRICT	nr_seq_cooperado_w
from	pls_cooperado
where	((cd_pessoa_fisica 	= cd_pessoa_fisica_p) or (cd_cgc		= cd_cgc_p))
and	to_date(dt_inclusao_p) < to_date(dt_exclusao)
and	nr_sequencia <> nr_sequencia_p;

if (nr_seq_cooperado_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265474,'SEQ_COOPERADO=' ||nr_seq_cooperado_w);
	--Não é permitido incluir este coooperado com a data de inclusão menor do que a data de exclusão ja existente! Seq ||nr_seq_cooperado_w
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
