SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_cad_oper_atend ( nr_seq_operador_p bigint, ie_situacao_p text, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Consistir se o operador já tem cadastro ativo
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  X] Tasy (Delphi/Java) [ ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qtd_operador_w 		bigint 	:= 	null;


BEGIN
/* Só consistir se está tentando incluir ou mudar para ativo */

if (ie_situacao_p = 'A') then
	select 	count(1)
	into STRICT	qtd_operador_w
	from 	pls_operador
	where 	cd_pessoa_fisica = cd_pessoa_fisica_p
	and (nr_sequencia <> nr_seq_operador_p or coalesce(nr_seq_operador_p::text, '') = '')
	and 	ie_situacao = 'A';

	if (qtd_operador_w	> 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(321048,null);
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
