SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_carencia_seg ( nr_seq_seguado_p bigint, nr_seq_tipo_carencia_p bigint, nr_seq_grupo_caren_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ie_cpt_w		varchar(10);
ie_utilizacao_w		varchar(10);
ie_sexo_carencia_w	varchar(10);
ds_erro_w		varchar(4000);


BEGIN

if (nr_seq_tipo_carencia_p IS NOT NULL AND nr_seq_tipo_carencia_p::text <> '') then
	select	max(ie_cpt)
	into STRICT	ie_cpt_w
	from	pls_tipo_carencia
	where	nr_sequencia	= nr_seq_tipo_carencia_p;

	if (ie_cpt_w = 'S') then
		ds_erro_w	:= ds_erro_w || wheb_mensagem_pck.get_texto(120942);
	end if;

	select   coalesce(pls_verificar_utilizacao_sca(nr_seq_tipo_carencia_p,3),'N')
	into STRICT	ie_utilizacao_w
	;

	if (ie_utilizacao_w =  'N') then
		ds_erro_w	:= ds_erro_w || wheb_mensagem_pck.get_texto(280120);
	end if;

	/*aaschlote 28/05/2013 - Tratamento realizado no uso da consistência as carências do contrato pelo JAVA*/

	if (nr_seq_seguado_p IS NOT NULL AND nr_seq_seguado_p::text <> '') then
		select	pls_consistir_sexo_carencia(nr_seq_seguado_p,nr_seq_tipo_carencia_p)
		into STRICT	ie_sexo_carencia_w
		;

		if (ie_sexo_carencia_w = 'N') then
			ds_erro_w	:= ds_erro_w|| wheb_mensagem_pck.get_texto(120958);
		end if;
	end if;
end if;

if (nr_seq_tipo_carencia_p IS NOT NULL AND nr_seq_tipo_carencia_p::text <> '') and (nr_seq_grupo_caren_p IS NOT NULL AND nr_seq_grupo_caren_p::text <> '') then
	ds_erro_w	:= ds_erro_w || wheb_mensagem_pck.get_texto(120948);
end if;

ds_erro_p	:= substr(ds_erro_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
