CREATE OR REPLACE PROCEDURE pls_consistir_caren_plan_inter ( nr_seq_plano_p bigint, nr_seq_carencia_p bigint, nr_seq_tipo_carencia_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ie_cpt_w		varchar(10);
ie_utilizacao_w		varchar(10);
ie_sexo_carencia_w	varchar(10);
ds_erro_w		varchar(4000);
qt_registros_w		bigint;


BEGIN

if (nr_seq_tipo_carencia_p IS NOT NULL AND nr_seq_tipo_carencia_p::text <> '') then
	select	max(ie_cpt)
	into STRICT	ie_cpt_w
	from	pls_tipo_carencia
	where	nr_sequencia	= nr_seq_tipo_carencia_p;

	if (ie_cpt_w = 'S') then
		ds_erro_w	:= ds_erro_w || wheb_mensagem_pck.get_texto(280334);
	end if;

	select   coalesce(pls_verificar_utilizacao_sca(nr_seq_tipo_carencia_p,3),'N')
	into STRICT	ie_utilizacao_w
	;

	if (ie_utilizacao_w =  'N') then
		ds_erro_w	:= ds_erro_w || wheb_mensagem_pck.get_texto(280335);
	end if;

	select	count(*)
	into STRICT	qt_registros_w
	from	pls_carencia
	where	nr_sequencia		<> nr_seq_carencia_p
	and	nr_seq_plano		= nr_seq_plano_p
	and	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_p;

	if (qt_registros_w > 0) then
		ds_erro_w	:= ds_erro_w || wheb_mensagem_pck.get_texto(280337);
	end if;
end if;

ds_erro_p	:= substr(ds_erro_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
