CREATE OR REPLACE PROCEDURE pls_consistir_cid_guia_req ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, nr_seq_prestador_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


CD_DOENCA_w		varchar(10);
qt_registro_w		bigint;

C01 CURSOR FOR
	SELECT	CD_DOENCA
	from	PLS_DIAGNOSTICO
	where	nr_seq_guia	= nr_seq_guia_p
	and	coalesce(nr_seq_requisicao_p::text, '') = ''
	and	(cd_doenca IS NOT NULL AND cd_doenca::text <> '')
	
union all

	SELECT	CD_DOENCA
	from	PLS_REQUISICAO_DIAGNOSTICO
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	and	coalesce(nr_seq_guia_p::text, '') = ''
	and	(cd_doenca IS NOT NULL AND cd_doenca::text <> '');


BEGIN

open C01;
loop
fetch C01 into
	CD_DOENCA_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(1)
	into STRICT	qt_registro_w
	from	cid_doenca
	where	upper(cd_doenca_cid)	= upper(CD_DOENCA_w)
	and	coalesce(ie_situacao,'A')	= 'A'  LIMIT 1;

	if (qt_registro_w = 0) then
		if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
			CALL pls_gravar_motivo_glosa('1509', nr_seq_guia_p, null,null,'CID ' || substr(obter_desc_cid(CD_DOENCA_w),1,50) || ' não existe ou está inativo',
						nm_usuario_p,'', 'CG', nr_seq_prestador_p,'',
						null);

		elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
			CALL pls_gravar_requisicao_glosa('1509', nr_seq_requisicao_p, null,null,'CID ' || substr(obter_desc_cid(CD_DOENCA_w),1,50) || ' não existe ou está inativo',
							nm_usuario_p,nr_seq_prestador_p, cd_estabelecimento_p, null,'');
		end if;
	end if;

	end;
end loop;
close C01;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
