SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_cliente_gc ( nr_seq_cliente_p bigint, cd_pessoa_fisica_p text, cd_cgc_p text, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


nr_seq_solicitacao_w		bigint;
cd_pf_vinculado_w		varchar(10);
cd_cnpj_vinculado_w		varchar(14);
ds_retorno_w			varchar(255) := '';


BEGIN

select	max(nr_seq_solicitacao)
into STRICT	nr_seq_solicitacao_w
from	pls_comercial_cliente
where	nr_sequencia	= nr_seq_cliente_p;

if (coalesce(nr_seq_solicitacao_w,0) <> 0) then
	select	cd_pf_vinculado,
		cd_cnpj_vinculado
	into STRICT	cd_pf_vinculado_w,
		cd_cnpj_vinculado_w
	from	pls_solicitacao_comercial
	where	nr_sequencia	= nr_seq_solicitacao_w;

	if (coalesce(cd_pf_vinculado_w,'0') <> '0') and (cd_pf_vinculado_w <> cd_pessoa_fisica_p) then
		ds_retorno_w	:= ds_retorno_w || 'A pessoa física informada é incompatível com a pessoa física vinculada na solicitação. Verifique!';
	end if;

	if (coalesce(cd_cnpj_vinculado_w,'0') <> '0') and (cd_cnpj_vinculado_w <> cd_cgc_p) then
		ds_retorno_w	:= ds_retorno_w || 'A pessoa jurídica informada é incompatível com a pessoa jurídica vinculada na solicitação. Verifique!';
	end if;
end if;

ds_retorno_p	:= ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
