SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_cod_mat ( cd_material_p bigint, cd_material_ops_p text, nr_seq_material_p bigint) AS $body$
DECLARE


qt_cod_mat_ops_w	bigint;
qt_cod_mat_w		bigint;


BEGIN
if (cd_material_ops_p IS NOT NULL AND cd_material_ops_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select count(1)
	into STRICT   qt_cod_mat_ops_w
	from   pls_material
	where  cd_material_ops 	= cd_material_ops_p
	and    cd_material	= cd_material_p
	and    nr_sequencia  	<> nr_seq_material_p
	and    coalesce(dt_exclusao::text, '') = '';

	if (qt_cod_mat_ops_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 267313, null ); /* Não é possível cadastrar dois materiais iguais. */
	end if;

	select count(1)
	into STRICT   qt_cod_mat_w
	from   pls_material
	where  cd_material   	<> cd_material_p
	and    nr_sequencia  	<> nr_seq_material_p
	and    cd_material_ops  = cd_material_ops_p
	and    coalesce(dt_exclusao::text, '') = '';

	if (qt_cod_mat_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 267314, null ); /* Já existe um material com o mesmo código associado a outro material hospitalar. */
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
