CREATE OR REPLACE PROCEDURE pls_consistir_compl_guia (nr_seq_requisicao_p bigint, nr_seq_segurado_p bigint, ie_tipo_saida_p text, nr_seq_prestador_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Consistir o complemento da guia na requisição para autorização
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/nr_seq_guia_w		bigint;
cd_doenca_w		varchar(10);
nr_seq_requisicao_w	bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_guia_plano a
	where	a.ie_tipo_guia = 3
	and	a.nr_seq_prestador = nr_seq_prestador_p
	and	a.nr_seq_segurado = nr_seq_segurado_p
	and	((0 = (SELECT count(1) from pls_diagnostico b where b.nr_seq_guia = a.nr_sequencia and (b.cd_doenca IS NOT NULL AND b.cd_doenca::text <> ''))) or (coalesce(a.ie_tipo_saida::text, '') = ''))
	and	trunc(a.dt_solicitacao, 'dd') =  trunc(clock_timestamp(), 'dd');


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_guia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		select	max(cd_doenca)
		into STRICT	cd_doenca_w
		from	pls_requisicao_diagnostico
		where	nr_seq_requisicao = nr_seq_requisicao_p;

		if (cd_doenca_w IS NOT NULL AND cd_doenca_w::text <> '' AND ie_tipo_saida_p IS NOT NULL AND ie_tipo_saida_p::text <> '') then
			CALL pls_salvar_compl_guia_web(nr_seq_guia_w, cd_doenca_w, ie_tipo_saida_p, nm_usuario_p, cd_estabelecimento_p);
		end if;
	end;
end loop;
close C01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
