SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_conta_hi ( nr_seq_conta_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/*PROCEDURE QUE NÃO POSSUI MAIS FINALIDADE*/
 
				 
cd_guia_w			varchar(20);
cd_motivo_glosa_w		varchar(10);
nr_seq_internacao_w		bigint;
nr_seq_conta_proc_w		bigint;
nr_seq_prestador_w		bigint;
ds_observacao_w			varchar(4000);
nr_seq_conta_proc_ww		bigint;
tx_item_w			double precision;
qt_apresentada_w		bigint;
ie_origem_proced_w		bigint;
cd_procedimento_w		bigint;
vl_apresentado_w		double precision;

/*Obter os procedimentos da internação*/
 
C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		cd_procedimento, 
		ie_origem_proced,      
		tx_item, 
		qt_procedimento_imp, 
		vl_unitario_imp       
	from	pls_conta_proc 
	where	nr_seq_conta = nr_seq_internacao_w 
	order by 1;

C02 CURSOR FOR 
	SELECT	substr(tiss_obter_motivo_glosa(nr_seq_motivo_glosa,'C'),1,10), 
		ds_observacao 
	from	pls_conta_glosa 
	where	nr_seq_conta_proc = nr_seq_conta_proc_w 
	and	substr(tiss_obter_motivo_glosa(nr_seq_motivo_glosa,'C'),1,10) in ('1820') 
	and	coalesce(nr_seq_conta_proc_ww,0) > 0 
	order by 1;


BEGIN 
 
select	coalesce(cd_guia_referencia, cd_guia), 
	nr_seq_prestador 
into STRICT	cd_guia_w, 
	nr_seq_prestador_w 
from	pls_conta 
where	nr_sequencia = nr_seq_conta_p;
 
begin 
select	nr_sequencia 
into STRICT	nr_seq_internacao_w 
from	pls_conta 
where	coalesce(cd_guia_referencia, cd_guia) = cd_guia_w 
and	ie_tipo_guia	= '5';
exception 
when others then 
	nr_seq_internacao_w := null;
end;
 
if (coalesce(nr_seq_internacao_w,0) <> 0) then 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_conta_proc_w, 
		cd_procedimento_w, 
		ie_origem_proced_w, 
		tx_item_w, 
		qt_apresentada_w, 
		vl_apresentado_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		/*A conta_proc da honorário que referencia a conta proc da internação*/
 
		begin 
		select	max(nr_sequencia) 
		into STRICT	nr_seq_conta_proc_ww 
		from	pls_conta_proc 
		where	nr_seq_conta		= nr_seq_conta_p 
		and	cd_procedimento		= cd_procedimento_w 
		and	ie_origem_proced 	= ie_origem_proced_w 
		and	tx_item			= tx_item_w 
		and	qt_procedimento_imp	= qt_apresentada_w 
		and	vl_unitario_imp		= vl_apresentado_w;		
		exception 
		when others then 
			nr_seq_conta_proc_ww	:= null;
		end;
		 
		open C02;
		loop 
		fetch C02 into	 
			cd_motivo_glosa_w, 
			ds_observacao_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin			 
			 
			CALL pls_gravar_conta_glosa(cd_motivo_glosa_w,null, nr_seq_conta_proc_ww, 
						null,'N', ds_observacao_w, 
						nm_usuario_p,'A','CC', 
						nr_seq_prestador_w, cd_estabelecimento_p, '',null);		
			 
			end;
		end loop;
		close C02;
		 
		end;
	end loop;
	close C01;	
	 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
