CREATE OR REPLACE PROCEDURE pls_consistir_contrato_grupo ( nr_seq_contrato_p bigint, nr_seq_contr_grupo_p bigint, nr_seq_grupo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

 
ie_grupos_diferente_w	varchar(1);
ie_grupo_contrat_dif_w	varchar(1);
qt_registros_w		integer;
ds_erro_w		varchar(4000)	:= null;
nr_contrato_principal_w	bigint;
nr_seq_contr_grupo_w	bigint;
ds_grupo_w		varchar(255);


BEGIN 
 
select	count(1) 
into STRICT	qt_registros_w 
from	pls_contrato_grupo 
where	nr_seq_contrato	= nr_seq_contrato_p 
and	nr_seq_grupo	= nr_seq_grupo_p 
and	nr_sequencia	<> nr_seq_contr_grupo_p;
 
if (qt_registros_w > 0) then 
	ds_erro_w := ds_erro_w || wheb_mensagem_pck.get_texto(292113,	'NR_CONTRATO=' || pls_obter_dados_contrato(nr_seq_contrato_p,'N') || 
									';NR_SEQ_GRUPO=' || nr_seq_grupo_p);
end if;
 
ie_grupos_diferente_w	:= coalesce(obter_valor_param_usuario(1278, 4, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p), 'N');
ie_grupo_contrat_dif_w	:= coalesce(obter_valor_param_usuario(1278, 5, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p), 'N');
 
if (ie_grupos_diferente_w	= 'S') then 
	select	count(1) 
	into STRICT	qt_registros_w 
	from	pls_contrato_grupo 
	where	nr_seq_contrato	= nr_seq_contrato_p 
	and	nr_sequencia	<> nr_seq_contr_grupo_p;
	 
	if (qt_registros_w	> 0) then 
		ds_erro_w := ds_erro_w || wheb_mensagem_pck.get_texto(280982, 'NR_SEQ_ESTIPULANTE_P=' || nr_seq_contrato_p || 
										';NR_SEQ_CONTRATO_P=' || substr(pls_obter_dados_contrato(nr_seq_contrato_p,'E'),1,255));
	end if;
end if;	
 
if (ie_grupo_contrat_dif_w	= 'S') and (coalesce(ds_erro_w::text, '') = '') then 
	select	max(nr_contrato_principal) 
	into STRICT	nr_contrato_principal_w 
	from	pls_contrato 
	where	nr_sequencia	= nr_seq_contrato_p;
	 
	if (nr_contrato_principal_w IS NOT NULL AND nr_contrato_principal_w::text <> '') then 
		select	max(nr_sequencia) 
		into STRICT	nr_seq_contr_grupo_w 
		from	pls_contrato_grupo 
		where	nr_seq_grupo	<> nr_seq_grupo_p 
		and	nr_seq_contrato	= nr_contrato_principal_w;
		 
		if (nr_seq_contr_grupo_w IS NOT NULL AND nr_seq_contr_grupo_w::text <> '') then 
			select	substr(pls_obter_dados_grupo_contrato(nr_seq_grupo,'N'),1,255) 
			into STRICT	ds_grupo_w 
			from	pls_contrato_grupo 
			where	nr_sequencia	= nr_seq_contr_grupo_w;
			 
			ds_erro_w := ds_erro_w || wheb_mensagem_pck.get_texto(280990, 'NR_SEQ_ESTIPULANTE_P=' || nr_contrato_principal_w || 
											';NR_SEQ_CONTRATO_P=' || substr(pls_obter_dados_contrato(nr_contrato_principal_w,'E'),1,255) || 
											';DS_GRUPO_P=' || ds_grupo_w);
		end if;
	end if;
end if;	
 
ds_erro_p	:= ds_erro_w;
 
commit;
 
end;
$body$
