CREATE OR REPLACE PROCEDURE pls_consistir_coop_seg_susp ( nr_seq_segurado_p bigint, ie_evento_p text, nr_sequencia_p bigint, ie_tipo_glosa_p text, nr_seq_prestador_p bigint, nr_seq_ocorrencia_p bigint, ds_parametro_um_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_congenere_w	bigint;
ie_situacao_atend_w	varchar(10);


BEGIN

select	max(nr_seq_congenere)
into STRICT	nr_seq_congenere_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

if (nr_seq_congenere_w IS NOT NULL AND nr_seq_congenere_w::text <> '') then
	select	coalesce(ie_situacao_atend,'A')
	into STRICT	ie_situacao_atend_w
	from	PLS_CONGENERE
	where	nr_sequencia	= nr_seq_congenere_w;

	if (ie_situacao_atend_w <> 'A') then
			CALL pls_gravar_glosa_tiss('9921',
			null, ie_evento_p,
			nr_sequencia_p, ie_tipo_glosa_p, nr_seq_prestador_p,
			nr_seq_ocorrencia_p, '', nm_usuario_p,
			cd_estabelecimento_p, null);
	end if;
end if;
/*
commit;Não pode haver commit em procedures intermediárias
*/
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
