SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_cpt_requisicao ( nr_seq_requisicao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Realizar a consistência de CPT(Cobertura Parcial Temporária) do beneficiário da requisição.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_segurado_w	bigint;
dt_inclusao_operadora_w	timestamp;
ie_tipo_guia_w		varchar(2);
nr_seq_cpt_w		bigint;
ie_liberado_w		varchar(1) := 'N';
qt_dias_w		integer;
dt_inicio_vigencia_w	timestamp;
nr_seq_prestador_w	bigint;
dt_carencia_w		timestamp;
dt_solicitacao_w	timestamp;
nr_seq_tipo_acomodacao_w bigint;

c01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.ie_liberado,
		c.qt_dias,
		c.dt_inicio_vigencia
	from	pls_carencia c,
		pls_tipo_carencia b,
		pls_carencia_proc a
	where	a.nr_seq_tipo_carencia	= b.nr_sequencia
	and	c.nr_seq_tipo_carencia	= b.nr_sequencia
	and	((coalesce(a.nr_seq_tipo_acomodacao::text, '') = '') or (a.nr_seq_tipo_acomodacao	= nr_seq_tipo_acomodacao_w))
	and	((coalesce(a.ie_tipo_guia::text, '') = '') or (ie_tipo_guia		= ie_tipo_guia_w))
	and	dt_inclusao_operadora_w between coalesce(a.dt_inicio_vigencia,dt_inclusao_operadora_w) and fim_dia(a.dt_fim_vigencia)
	and	c.nr_sequencia	in (	SELECT	x.nr_sequencia
						from	pls_carencia x
						where	x.nr_seq_segurado		= nr_seq_segurado_w)
	and (a.cd_doenca_cid in (	select	x.cd_doenca
						from	pls_requisicao_diagnostico x
						where	x.nr_seq_requisicao 		= nr_seq_requisicao_p) or coalesce(a.cd_doenca_cid::text, '') = '')
	and	((trim(both a.CD_CATEGORIA_CID) in (	select	y.CD_CATEGORIA_CID
						from	pls_requisicao_diagnostico x,
							CID_DOENCA			y
						where	x.cd_doenca	= y.cd_doenca_cid
						and	x.nr_seq_requisicao	= nr_seq_requisicao_p)) or (coalesce(trim(both a.CD_CATEGORIA_CID)::text, '') = '')) /*aaschlote - Coloquei o trim pois colocava um espaço vazio no final*/
	and	coalesce(a.cd_procedimento::text, '') = ''
	and	coalesce(a.cd_grupo_proc::text, '') = ''
	and	coalesce(a.cd_especialidade::text, '') = ''
	and	coalesce(a.cd_area_procedimento::text, '') = ''
	and	coalesce(a.nr_seq_grupo_servico::text, '') = ''
	and	b.ie_cpt		= 'S'
	and	b.ie_situacao		= 'A';


BEGIN

begin
select	nr_seq_segurado,
	ie_tipo_guia,
	nr_seq_prestador,
	coalesce(dt_requisicao,clock_timestamp()),
	nr_seq_tipo_acomodacao
into STRICT	nr_seq_segurado_w,
	ie_tipo_guia_w,
	nr_seq_prestador_w,
	dt_solicitacao_w,
	nr_seq_tipo_acomodacao_w
from	pls_requisicao
where	nr_sequencia	= nr_seq_requisicao_p;
exception
when others then
	nr_seq_segurado_w		:= null;
	ie_tipo_guia_w			:= null;
	nr_seq_prestador_w		:= null;
	dt_solicitacao_w		:= null;
	nr_seq_tipo_acomodacao_w	:= null;
end;

begin
select	dt_inclusao_operadora
into STRICT	dt_inclusao_operadora_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_w;
exception
when others then
	dt_inclusao_operadora_w	:= null;
end;

open c01;
loop
fetch c01 into
	nr_seq_cpt_w,
	ie_liberado_w,
	qt_dias_w,
	dt_inicio_vigencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

if (ie_liberado_w	= 'S') then
	select (coalesce(dt_inicio_vigencia_w,dt_inclusao_operadora_w) + qt_dias_w)
	into STRICT	dt_carencia_w
	;

	if (coalesce(dt_solicitacao_w,clock_timestamp()) < dt_carencia_w) then
		CALL pls_gravar_requisicao_glosa(	'1421', nr_seq_requisicao_p, null,
						null, 'CPT - Liberado a partir de '||to_char(dt_carencia_w,'dd/mm/yyyy'), nm_usuario_p,
						nr_seq_prestador_w, cd_estabelecimento_p, null,
						'');
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_consistir_cpt_requisicao ( nr_seq_requisicao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

